mapper.select:
select

from AEA_HI_CERTINST


        AND CERTINST_ID = #{certinstId}


        AND CERT_ID = #{certId}


        AND UNIT_INFO_ID = #{unitInfoId}


        AND PROJ_INFO_ID = #{projInfoId}


        AND ATT_LINK_ID = #{attLinkId}


        AND CREATER = #{creater}


        AND CREATE_TIME = #{createTime}


        AND MODIFIER = #{modifier}


        AND MODIFY_TIME = #{modifyTime}


        AND CERTINST_CODE = #{certinstCode}


        AND CERTINST_NAME = #{certinstName}


        AND ISSUE_DATE = #{issueDate}


        AND TERM_START = #{termStart}


        AND TERM_END = #{termEnd}


        AND CA_INFO = #{caInfo}


        AND CERT_OWNER = #{certOwner}


        AND ISSUE_ORG_ID = #{issueOrgId}


        AND TERM_NUM = #{termNum}


        AND TERM_UNIT = #{termUnit}


        AND FLXL = #{flxl}


        AND APPLYINST_ID = #{applyinstId}


        AND LINKMAN_INFO_ID = #{linkmanInfoId}


        AND QUAL_LEVEL_ID = #{qualLevelId}


        AND ROOT_ORG_ID = #{rootOrgId}


        AND AUTH_CODE = #{authCode}


        AND certinst_source = #{certinstSource}

select aui.APPLICANT issueOrgName,
from AEA_HI_CERTINST ahc LEFT JOIN aea_unit_info aui on aui.UNIT_INFO_ID = ahc.UNIT_INFO_ID where ahc.CERTINST_ID in
#{id}

SELECT ahc.CERTINST_ID certinstId, ahc.CERTINST_NAME certinstName, ahc.TERM_START termStart, ahc.TERM_END termEnd, ahc.MANAGEMENT_SCOPE managementScope, ahc.ISSUE_DATE issueDate, ahc.certinst_source certinstSource, (SELECT o.ORG_NAME FROM OPU_OM_ORG o WHERE o.ORG_ID = ahc.ISSUE_ORG_ID) orgName FROM AEA_HI_CERTINST ahc WHERE ahc.CERTINST_ID IN ( SELECT abc.CERTINST_ID FROM aea_bus_certinst abc, aea_im_unit_service aius WHERE abc.BUS_RECORD_ID = aius.UNIT_SERVICE_ID AND abc.BUS_TABLE_NAME = 'AEA_IM_UNIT_SERVICE' AND abc.PK_NAME = 'UNIT_SERVICE_ID' AND abc.IS_DELETE = '0' AND aius.IS_DELETE = '0' AND aius.AUDIT_FLAG = '1' AND aius.UNIT_INFO_ID = #{unitInfoId}
AND aius.UNIT_SERVICE_ID = #{unitServiceId}
)

SELECT
, aiql.QUAL_LEVEL_NAME qualLevelName, (SELECT aiq.QUAL_NAME FROM aea_im_qual aiq WHERE aiq.QUAL_ID = temp.QUAL_ID) qualName FROM AEA_HI_CERTINST ahc LEFT JOIN aea_im_qual_level aiql on aiql.QUAL_LEVEL_ID = ahc.QUAL_LEVEL_ID LEFT JOIN ( SELECT aicm.CERTINST_ID, MAX(aism.QUAL_ID) QUAL_ID FROM aea_im_certinst_major aicm, aea_im_service_major aism WHERE aicm.MAJOR_ID = aism.MAJOR_ID AND aicm.IS_DELETE = '0' AND aism.IS_DELETE = '0' GROUP BY aicm.CERTINST_ID) temp ON ahc.CERTINST_ID = temp.CERTINST_ID WHERE ahc.CERTINST_ID IN ( SELECT abc.CERTINST_ID FROM aea_bus_certinst abc
, aea_im_unit_service aius WHERE abc.BUS_RECORD_ID = aius.UNIT_SERVICE_ID AND abc.BUS_TABLE_NAME = 'AEA_IM_UNIT_SERVICE' AND abc.PK_NAME = 'UNIT_SERVICE_ID' AND abc.IS_DELETE = '0' AND aius.IS_DELETE = '0' AND aius.AUDIT_FLAG = '1' AND aius.UNIT_INFO_ID = #{typeId}
, aea_im_service_linkman aisl WHERE abc.BUS_RECORD_ID = aisl.SERVICE_LINKMAN_ID AND UPPER(abc.BUS_TABLE_NAME) = 'AEA_IM_SERVICE_LINKMAN' AND UPPER(abc.PK_NAME) = 'SERVICE_LINKMAN_ID' AND abc.IS_DELETE = '0' AND aisl.IS_DELETE = '0' AND aisl.AUDIT_FLAG = '1' AND aisl.LINKMAN_INFO_ID = #{typeId}
)

select

from aea_hi_certinst hicert
where hicert.LINKMAN_INFO_ID = #{linkmanInfoId}

select



from aea_hi_certinst ahc
where ahc.CERTINST_CODE=#{certinstCode}
and ahc.ROOT_ORG_ID=#{currentOrgId}
