select
, aps.stage_name stageName , apt.theme_id themeId , apt.theme_name themeName , api.proj_name projName , api.gcbm gcbm from aea_hi_guide ahg join aea_par_stage aps on ahg.stage_id=aps.stage_id join aea_par_theme_ver aptv on aptv.theme_ver_id=aps.theme_ver_id join aea_par_theme apt on apt.theme_id=aptv.theme_id join aea_proj_info api on api.proj_info_id=ahg.proj_info_id
(exists ( select 1 from aea_hi_guide_detail ahgd where ahgd.guide_id=ahg.guide_id and ahgd.guide_user_id=#{currentUserId} ) or ahg.LEADER_USER_ID=#{currentUserId} )
and ( apt.theme_name like concat(#{keyword}, '%') or api.proj_name like concat(#{keyword}, '%') or api.gcbm like concat(#{keyword}, '%') )
and ahg.apply_state=#{applyState}
and ahg.GUIDE_START_TIME >= #{guideStartTime}
and ahg.GUIDE_END_TIME <= #{guideEndTime}

