mapper.select:
select
,ouser.USER_NAME createrName
from AEA_HI_ITEM_CORRECT ahic
LEFT JOIN OPU_OM_USER ouser ON ouser.USER_ID = ahic.CREATER


    AND ahic.CORRECT_ID = #{correctId}


    AND ahic.ITEMINST_ID = #{iteminstId}


    AND ahic.APPLYINST_ID = #{applyinstId}


    AND ahic.PROJ_INFO_ID = #{projInfoId}


    AND ahic.IS_FLOW_TRIGGER = #{isFlowTrigger}


    AND ahic.APPINST_ID = #{appinstId}


    AND ahic.CORRECT_DUE_DAYS = #{correctDueDays}


    AND ahic.CORRECT_DUE_TIME = #{correctDueTime}


    AND ahic.MATINST_IDS = #{matinstIds}


    AND ahic.CORRECT_DESC = #{correctDesc}


    AND ahic.CORRECT_MEMO = #{correctMemo}


    AND ahic.CHARGE_ORG_ID = #{chargeOrgId}


    AND ahic.CHARGE_ORG_NAME = #{chargeOrgName}


    AND ahic.WINDOW_USER_ID = #{windowUserId}


    AND ahic.WINDOW_USER_NAME = #{windowUserName}


    AND ahic.WINDOW_PHONE = #{windowPhone}


    AND ahic.OPS_USER_ID = #{opsUserId}


    AND ahic.OPS_USER_NAME = #{opsUserName}


    AND ahic.OPS_TIME = #{opsTime}


    AND ahic.CORRECT_USER_NAME = #{correctUserName}


    AND ahic.CORRECT_END_TIME = #{correctEndTime}


    AND ahic.PRINT_USER_ID = #{printUserId}


    AND ahic.PRINT_USER_NAME = #{printUserName}


    AND ahic.PRINT_TIME = #{printTime}


    AND ahic.CORRECT_STATE = #{correctState}


    AND ahic.IS_DELETED = #{isDeleted}


    AND ahic.CREATER = #{creater}


    AND ahic.CREATE_TIME = #{createTime}


    AND ahic.MODIFIER = #{modifier}


    AND ahic.MODIFY_TIME = #{modifyTime}


    AND ahic.ROOT_ORG_ID = #{rootOrgId}

AND ahic.IS_DELETED = '0'

ORDER BY ahic.CREATE_TIME DESC

SELECT ahi_123.ITEMINST_ID iteminstId, ahi_123.ITEMINST_CODE iteminstCode, ahap_123.APPLYINST_CODE applyinstCode, temp1.PROJ_NAME projName, temp1.LOCAL_CODE AS localCode, temp1.GCBM AS gcbm, '并联' isSeriesApprove, theme.THEME_NAME themeName, aps_123.STAGE_NAME stageName, ahi_123.ITEMINST_NAME iteminstName, ahap_123.CREATE_TIME createTime, ahap_123.APPLY_SUBJECT applySubject, ahic_123.CORRECT_ID correctId, ahic_123.CORRECT_MEMO correctMemo FROM AEA_HI_ITEMINST ahi_123 LEFT JOIN AEA_HI_PAR_STAGEINST ahs_123 ON ahi_123.STAGEINST_ID = ahs_123.STAGEINST_ID LEFT JOIN AEA_HI_APPLYINST ahap_123 ON ahs_123.APPLYINST_ID = ahap_123.APPLYINST_ID LEFT JOIN AEA_PAR_STAGE aps_123 ON aps_123.STAGE_ID = ahs_123.STAGE_ID LEFT JOIN AEA_PAR_THEME_VER ver ON aps_123.THEME_VER_ID = ver.THEME_VER_ID LEFT JOIN AEA_PAR_THEME theme ON theme.THEME_ID = ver.THEME_ID INNER JOIN AEA_HI_ITEM_CORRECT ahic_123 ON ahic_123.APPLYINST_ID = ahap_123.APPLYINST_ID AND ahic_123.ITEMINST_ID = ahi_123.ITEMINST_ID AND ahic_123.CORRECT_STATE = '6' LEFT JOIN ( SELECT ap.APPLYINST_ID, p.PROJ_NAME AS PROJ_NAME, p.LOCAL_CODE AS LOCAL_CODE, p.GCBM AS GCBM FROM aea_applyinst_proj ap LEFT JOIN aea_proj_info p ON ap.proj_info_id = p.proj_info_id GROUP BY ap.APPLYINST_ID ) temp1 ON temp1.APPLYINST_ID = ahap_123.APPLYINST_ID WHERE ahi_123.IS_SERIES_APPROVE = '0' AND ahi_123.ITEMINST_STATE = '6' AND ( ( ahap_123.APPLY_SUBJECT = '0'
AND ahap_123.LINKMAN_INFO_ID = #{userId}
AND ahap_123.LINKMAN_INFO_ID is null
) OR ( ahap_123.APPLY_SUBJECT = '1' AND ahap_123.APPLYINST_ID IN ( SELECT aaup.APPLYINST_ID FROM aea_applyinst_unit_proj aaup LEFT JOIN aea_unit_proj aup ON aaup.UNIT_PROJ_ID = aup.UNIT_PROJ_ID WHERE aaup.IS_DELETED != 1 and aup.is_Owner = 1 and aup.IS_DELETED != 1
and aup.UNIT_INFO_ID = #{unitInfoId}
and aup.UNIT_INFO_ID is null
) ) )
AND ( ahi_123.ITEMINST_NAME LIKE CONCAT(CONCAT('%',#{keyword}),'%') or ahap_123.APPLYINST_CODE LIKE CONCAT(CONCAT('%',#{keyword}),'%') OR temp1.PROJ_NAME LIKE CONCAT(CONCAT('%',#{keyword}),'%')OR ahi_123.ITEMINST_CODE LIKE CONCAT(CONCAT('%',#{keyword}),'%') )
UNION SELECT ahi_123.ITEMINST_ID iteminstId, ahi_123.ITEMINST_CODE iteminstCode, ahap_123.APPLYINST_CODE applyinstCode, temp1.PROJ_NAME projName, temp1.LOCAL_CODE AS localCode, temp1.GCBM AS gcbm, '单项' IS_SERIES_APPROVE, temp1.THEME_NAME AS themeName, NULL AS STAGE_NAME, ahi_123.ITEMINST_NAME iteminstName, ahap_123.CREATE_TIME createTime, ahap_123.APPLY_SUBJECT applySubject, ahic_123.CORRECT_ID correctId, ahic_123.CORRECT_MEMO correctMemo FROM AEA_HI_ITEMINST ahi_123 LEFT JOIN AEA_HI_SERIESINST ahs_123 ON ahi_123.SERIESINST_ID = ahs_123.SERIESINST_ID LEFT JOIN AEA_HI_APPLYINST ahap_123 ON ahs_123.APPLYINST_ID = ahap_123.APPLYINST_ID LEFT JOIN AEA_LINKMAN_INFO ali_123 ON ali_123.LINKMAN_INFO_ID = ahap_123.LINKMAN_INFO_ID INNER JOIN AEA_HI_ITEM_CORRECT ahic_123 ON ahic_123.APPLYINST_ID = ahap_123.APPLYINST_ID AND ahic_123.ITEMINST_ID = ahi_123.ITEMINST_ID AND ahic_123.CORRECT_STATE = '6' LEFT JOIN ( SELECT ap.APPLYINST_ID, p.PROJ_NAME AS PROJ_NAME, p.LOCAL_CODE AS LOCAL_CODE, apt.THEME_NAME AS THEME_NAME, p.GCBM AS GCBM FROM aea_applyinst_proj ap LEFT JOIN aea_proj_info p ON ap.proj_info_id = p.proj_info_id LEFT JOIN aea_par_theme apt on apt.theme_id = p.theme_id GROUP BY ap.APPLYINST_ID ) temp1 ON temp1.APPLYINST_ID = ahap_123.APPLYINST_ID WHERE ahi_123.IS_SERIES_APPROVE = '1' AND ahi_123.ITEMINST_STATE = '6' AND ( ( ahap_123.APPLY_SUBJECT = '0' AND ahap_123.LINKMAN_INFO_ID = #{userId} ) OR ( ahap_123.APPLY_SUBJECT = '1' AND ahap_123.APPLYINST_ID IN ( SELECT aaup.APPLYINST_ID FROM aea_applyinst_unit_proj aaup LEFT JOIN aea_unit_proj aup ON aaup.UNIT_PROJ_ID = aup.UNIT_PROJ_ID WHERE aaup.IS_DELETED != 1 and aup.is_Owner = 1 and aup.IS_DELETED != 1
and aup.UNIT_INFO_ID = #{unitInfoId}
and aup.UNIT_INFO_ID is null
) ) )
AND ( ahi_123.ITEMINST_NAME LIKE CONCAT(CONCAT('%',#{keyword}),'%') or ahap_123.APPLYINST_CODE LIKE CONCAT(CONCAT('%',#{keyword}),'%') OR temp1.PROJ_NAME LIKE CONCAT(CONCAT('%',#{keyword}),'%')OR ahi_123.ITEMINST_CODE LIKE CONCAT(CONCAT('%',#{keyword}),'%') )
