select

from AEA_HI_ITEM_INOUTINST


        AND INOUTINST_ID = #{inoutinstId}


        AND ITEMINST_ID = #{iteminstId}


        AND ITEM_INOUT_ID = #{itemInoutId}


        AND MATINST_ID = #{matinstId}


        AND CERTINST_ID = #{certinstId}


        AND CREATER = #{creater}


        AND CREATE_TIME = #{createTime}


        AND MODIFIER = #{modifier}


        AND MODIFY_TIME = #{modifyTime}


        AND IS_COLLECTED = #{isCollected}


        AND IS_PAR_IN = #{isParIn}


        AND PAR_IN_ID = #{parInId}


        AND ROOT_ORG_ID = #{rootOrgId}

select  from aea_hi_item_inoutinst ahii ,
aea_item_inout aii
where ahii.ITEMINST_ID = #{iteminstId}
and ahii.ITEM_INOUT_ID=aii.INOUT_ID
and aii.IS_INPUT='0'
and ahii.ROOT_ORG_ID = #{rootOrgId}
and aii.ROOT_ORG_ID=#{rootOrgId}
and aii.IS_DELETED='0'

select



from aea_hi_item_inoutinst ahii ,(
select aii.inout_id from aea_hi_iteminst ahi ,aea_item_inout aii where ahi.ITEMINST_ID =#{iteminstId}
and aii.FILE_TYPE='cert' and aii.IS_INPUT='0' and aii.IS_DELETED='0' and ahi.ITEM_VER_ID = aii.ITEM_VER_ID
) gg where gg.inout_id = ahii.ITEM_INOUT_ID

select



from aea_hi_item_inoutinst ahii ,(
select aii.inout_id from aea_hi_iteminst ahi ,aea_item_inout aii where ahi.ITEMINST_ID in
 #{iteminstId}
and aii.FILE_TYPE='cert' and aii.IS_INPUT='0' and aii.IS_DELETED='0' and ahi.ITEM_VER_ID = aii.ITEM_VER_ID
) gg where gg.inout_id = ahii.ITEM_INOUT_ID

SELECT ahim.MATINST_ID as matinstId, ahim.MATINST_NAME as matinstName, ahi1.ITEMINST_NAME as iteminstName, ahi1.ITEM_VER_ID as itemVerId, ahi1.ITEMINST_ID as iteminstId, CASE WHEN ahim.ATT_COUNT IS NULL THEN 0 ELSE ahim.ATT_COUNT END as attCount, CASE WHEN ahim.REAL_COPY_COUNT IS NULL THEN 0 ELSE ahim.REAL_COPY_COUNT END as realCopyCount, CASE WHEN ahim.REAL_PAPER_COUNT IS NULL THEN 0 ELSE ahim.REAL_PAPER_COUNT END as realPaperCount, ahii.INOUTINST_ID as inoutinstId, ahii.IS_COLLECTED as isCollected, ahii.IS_PAR_IN as isParIn, ahii.ITEMINST_ID as iteminstId, ahii.PAR_IN_ID as parInId, aim.MAT_ID as matId, aim.MAT_CODE as matCode, aim.MAT_FROM as matFrom, aim.IS_COMMON as isCommon, aim.DUE_PAPER_COUNT as duePaperCount, aim.DUE_COPY_COUNT as dueCopyCount, ahim.OFFICIAL_DOC_DUE_DATE as officialDocDueDate, ahim.OFFICIAL_DOC_NO as officialDocNo, ahim.OFFICIAL_DOC_TITLE as officialDocTitle, aim.IS_OFFICIAL_DOC as isOfficeDoc, aim.ATT_IS_REQUIRE as attIsRequire FROM AEA_HI_ITEM_INOUTINST ahii LEFT JOIN AEA_HI_ITEMINST ahi1 ON ahi1.ITEMINST_ID = ahii.ITEMINST_ID LEFT JOIN AEA_HI_ITEM_MATINST ahim ON ahii.MATINST_ID = ahim.MATINST_ID LEFT JOIN AEA_ITEM_MAT aim ON aim.MAT_ID = ahim.MAT_ID WHERE ahii.ITEMINST_ID = #{iteminstId} and aim.IS_DELETED='0'
and aim.IS_OFFICIAL_DOC=#{isOfficeDoc}
ORDER BY aim.SORT_NO

SELECT ahim.MATINST_ID as matinstId, ahim.MATINST_NAME as matinstName, ahi1.ITEMINST_NAME as iteminstName, ahi1.ITEM_VER_ID as itemVerId, ahi1.ITEMINST_ID as iteminstId, CASE WHEN ahim.ATT_COUNT IS NULL THEN 0 ELSE ahim.ATT_COUNT END as attCount, CASE WHEN ahim.REAL_COPY_COUNT IS NULL THEN 0 ELSE ahim.REAL_COPY_COUNT END as realCopyCount, CASE WHEN ahim.REAL_PAPER_COUNT IS NULL THEN 0 ELSE ahim.REAL_PAPER_COUNT END as realPaperCount, ahii.INOUTINST_ID as inoutinstId, ahii.IS_COLLECTED as isCollected, ahii.IS_PAR_IN as isParIn, ahii.ITEMINST_ID as iteminstId, ahii.PAR_IN_ID as parInId, aim.MAT_ID as matId, aim.MAT_CODE as matCode, aim.MAT_FROM as matFrom, aim.IS_COMMON as isCommon, aim.DUE_PAPER_COUNT as duePaperCount, aim.DUE_COPY_COUNT as dueCopyCount, ahim.OFFICIAL_DOC_DUE_DATE as officialDocDueDate, ahim.OFFICIAL_DOC_NO as officialDocNo, ahim.OFFICIAL_DOC_TITLE as officialDocTitle, aim.IS_OFFICIAL_DOC as isOfficeDoc, aim.ATT_IS_REQUIRE as attIsRequire FROM AEA_HI_ITEM_INOUTINST ahii LEFT JOIN AEA_HI_ITEMINST ahi1 ON ahi1.ITEMINST_ID = ahii.ITEMINST_ID LEFT JOIN AEA_HI_ITEM_MATINST ahim ON ahii.MATINST_ID = ahim.MATINST_ID LEFT JOIN AEA_ITEM_MAT aim ON aim.MAT_ID = ahim.MAT_ID WHERE ahii.ITEMINST_ID IN ( SELECT ahi.ITEMINST_ID FROM AEA_HI_ITEMINST ahi WHERE ahi.STAGEINST_ID IN ( SELECT ahi1.STAGEINST_ID FROM AEA_HI_ITEMINST ahi1 WHERE ahi1.ITEMINST_ID = #{iteminstId}) ) and aim.IS_DELETED='0'
and aim.IS_OFFICIAL_DOC=#{isOfficeDoc}
ORDER BY aim.SORT_NO

SELECT ahim.MATINST_ID as matinstId, ahim.MATINST_NAME as matinstName, ahi1.ITEMINST_NAME as iteminstName, ahi1.ITEM_VER_ID as itemVerId, ahi1.ITEMINST_ID as iteminstId, CASE WHEN ahim.ATT_COUNT IS NULL THEN 0 ELSE ahim.ATT_COUNT END as attCount, CASE WHEN ahim.REAL_COPY_COUNT IS NULL THEN 0 ELSE ahim.REAL_COPY_COUNT END as realCopyCount, CASE WHEN ahim.REAL_PAPER_COUNT IS NULL THEN 0 ELSE ahim.REAL_PAPER_COUNT END as realPaperCount, ahii.INOUTINST_ID as inoutinstId, ahii.IS_COLLECTED as isCollected, ahii.IS_PAR_IN as isParIn, ahii.ITEMINST_ID as iteminstId, ahii.PAR_IN_ID as parInId, aim.MAT_ID as matId, aim.MAT_CODE as matCode, aim.MAT_FROM as matFrom, aim.IS_COMMON as isCommon, aim.DUE_PAPER_COUNT as duePaperCount, aim.DUE_COPY_COUNT as dueCopyCount, ahim.OFFICIAL_DOC_DUE_DATE as officialDocDueDate, ahim.OFFICIAL_DOC_NO as officialDocNo, ahim.OFFICIAL_DOC_TITLE as officialDocTitle, aim.IS_OFFICIAL_DOC as isOfficeDoc, aim.ATT_IS_REQUIRE as attIsRequire FROM AEA_HI_ITEM_INOUTINST ahii LEFT JOIN AEA_HI_ITEMINST ahi1 ON ahi1.ITEMINST_ID = ahii.ITEMINST_ID LEFT JOIN AEA_HI_ITEM_MATINST ahim ON ahii.MATINST_ID = ahim.MATINST_ID LEFT JOIN AEA_ITEM_MAT aim ON aim.MAT_ID = ahim.MAT_ID where ahii.ITEMINST_ID in
#{iteminstId}
AND aim.IS_OFFICIAL_DOC = #{isOfficeDoc}
ORDER BY aim.SORT_NO

select ahim.MATINST_ID as matinstId, ahim.MATINST_NAME as matinstName, CASE WHEN ahim.ATT_COUNT IS NULL THEN 0 ELSE ahim.ATT_COUNT END as attCount, CASE WHEN ahim.REAL_COPY_COUNT IS NULL THEN 0 ELSE ahim.REAL_COPY_COUNT END as realCopyCount, CASE WHEN ahim.REAL_PAPER_COUNT IS NULL THEN 0 ELSE ahim.REAL_PAPER_COUNT END as realPaperCount, aim.DUE_PAPER_COUNT as duePaperCount, aim.DUE_COPY_COUNT as dueCopyCount, ahim.OFFICIAL_DOC_DUE_DATE as officialDocDueDate, ahim.OFFICIAL_DOC_NO as officialDocNo, ahim.OFFICIAL_DOC_TITLE as officialDocTitle, aim.IS_OFFICIAL_DOC as isOfficeDoc, aim.ATT_IS_REQUIRE as attIsRequire, aim.PAPER_IS_REQUIRE as paperIsRequire from AEA_HI_ITEM_MATINST ahim inner join AEA_ITEM_MAT aim on aim.MAT_ID=ahim.MAT_ID inner join AEA_HI_ITEM_INOUTINST ahii on ahii.MATINST_ID=ahim.MATINST_ID where ahii.ITEMINST_ID=#{iteminstId} and aim.IS_OFFICIAL_DOC!='1' and ahim.MAT_ID IN
#{item}
#{item}
ORDER BY aim.SORT_NO
