mapper.select:
select

from AEA_HI_RECEIVE


        AND RECEIVE_ID = #{receiveId}


        AND APPLYINST_ID = #{applyinstId}


        AND OUTINST_ID = #{outinstId}


        AND RECEIVE_USER_NAME = #{receiveUserName}


        AND RECEIVE_CERT_NO = #{receiveCertNo}


        AND RECEIVE_MEMO = #{receiveMemo}


        AND APPROVE_DEPT_REGION = #{approveDeptRegion}


        AND SUB_DEPT_REGION = #{subDeptRegion}


        AND RECEIVE_USER_MOBILE = #{receiveUserMobile}


        AND SERVICE_ADDRESS = #{serviceAddress}


        AND DOCUMENT_NUM = #{documentNum}


        AND RECEIPT_TYPE = #{receiptType}


        AND ROOT_ORG_ID = #{rootOrgId}

select

from AEA_HI_RECEIVE
where APPLYINST_ID = #{applyinstId}

    AND RECEIPT_TYPE=#{receiveType}

select applyinst.APPLYINST_ID, applyinst.APPLYINST_CODE, applyinst.IS_SERIES_APPROVE, case applyinst.IS_SERIES_APPROVE when '0' then (select stage_name from aea_hi_par_stageinst stageinst LEFT JOIN aea_par_stage stage on stage.STAGE_ID=stageinst.STAGE_ID where stageinst.APPLYINST_ID in #{applyinstId} ) else ( select ITEMINST_NAME from aea_hi_iteminst iteminst join aea_hi_seriesinst seriesinst on seriesinst.SERIESINST_ID=iteminst.SERIESINST_ID where seriesinst.APPLYINST_ID in #{applyinstId}
) end name , receive.* from aea_hi_applyinst applyinst
LEFT JOIN aea_hi_receive receive on receive.APPLYINST_ID=applyinst.APPLYINST_ID where applyinst.APPLYINST_ID in #{applyinstId}
order by receive.RECEIPT_TYPE

select

from AEA_HI_RECEIVE


        APPLYINST_ID in
        #{applyinstId}



        and RECEIPT_TYPE in
        #{receiptType}
