mapper.select:
select

from AEA_IM_CLIENT_SERVICE


        AND CLIENT_SERVICE_ID = #{clientServiceId}


        AND UNIT_SERVICE_ID = #{unitServiceId}


        AND UNIT_LINKMAN_ID = #{unitLinkmanId}


        AND IS_DELETED = #{isDeleted}


        AND CREATER = #{creater}


        AND CREATE_TIME = #{createTime}

    AND IS_DELETED = '0'
