mapper.select:
select
,
(
SELECT MAX(aibp.PRICE) FROM aea_im_bidding_price aibp
WHERE aibp.IS_DELETE = '0' AND aibp.IS_CHOICE = '1'
AND aibp.UNIT_BIDDING_ID = UNIT_BIDDING_ID
) realPrice
from AEA_IM_UNIT_BIDDING


        AND UNIT_BIDDING_ID = #{unitBiddingId}


        AND PROJ_PURCHASE_ID = #{projPurchaseId}


        AND UNIT_INFO_ID = #{unitInfoId}


        AND IS_WON_BID = #{isWonBid}


        AND AUDIT_FLAG = #{auditFlag}


        AND IS_DELETE = #{isDelete}


        AND CREATER = #{creater}


        AND CREATE_TIME = #{createTime}


        AND MODIFIER = #{modifier}


        AND MODIFY_TIME = #{modifyTime}


        AND LINKMAN_INFO_ID = #{linkmanInfoId}


        AND MEMO = #{memo}


        AND UNIT_SERVICE_ID = #{unitServiceId}


        AND BIDDING_TIME = #{biddingTime}


        AND IS_CANCEL_SIGNUP = #{isCancelSignup}



        AND IS_UPLOAD_CONTRACT = #{isUploadContract}



        AND IS_UPLOAD_RESULT = #{isUploadResult}



        AND IS_EVALUATE = #{isEvaluate}



        AND ROOT_ORG_ID = #{rootOrgId}

SELECT aipp.PROJ_PURCHASE_ID projPurchaseId, api.PROJ_NAME projName, (CASE aipp.AUDIT_FLAG WHEN '0' THEN '未提交' WHEN '1' THEN '服务中' WHEN '2' THEN '服务完成' WHEN '3' THEN '服务中止' WHEN '4' THEN '审核中' WHEN '5' THEN '退回' WHEN '6' THEN '报名中' WHEN '7' THEN '选取中' WHEN '8' THEN '选取开始' WHEN '9' THEN '已选取' WHEN '10' THEN '无效' ELSE '' END) auditFlag, (SELECT aui.APPLICANT FROM aea_unit_info aui WHERE aui.UNIT_INFO_ID = aipp.PUBLISH_UNIT_INFO_ID) applicant, aipp.BIDDING_TYPE biddingType, aise.COMP_EVALUATION compEvaluation, aise.SERVICE_QUALITY serviceQuality, aise.SERVICE_PRESCRIPTION servicePrescription, aise.SERVICE_ATTITUDE serviceAttitude, aise.SERVICE_CHARGE serviceCharge, aise.SERVICE_STANDARD serviceStandard, aise.MEMO memo FROM aea_im_unit_bidding aiub LEFT JOIN aea_im_service_evaluation aise ON aiub.UNIT_BIDDING_ID = aise.UNIT_BIDDING_ID AND aise.IS_DELETE = '0' LEFT JOIN aea_im_proj_purchase aipp ON aiub.PROJ_PURCHASE_ID = aipp.PROJ_PURCHASE_ID LEFT JOIN aea_proj_info api ON api.PROJ_INFO_ID = aipp.PROJ_INFO_ID WHERE aiub.IS_WON_BID = '1' AND aiub.IS_CANCEL_SIGNUP = '0' AND aiub.IS_DELETE = '0' AND aipp.IS_DELETE = '0' AND aiub.UNIT_INFO_ID = #{unitInfoId}
AND api.PROJ_NAME LIKE concat('%',concat(#{projName},'%'))
AND aipp.SERVICE_ID = #{serviceId}

SELECT aiub.UNIT_BIDDING_ID unitBiddingId, aipp.PROJ_PURCHASE_ID projPurchaseId, aipp.EXPIRATION_DATE expirationDate, aiub.IS_UPLOAD_CONTRACT isUploadContract, aiub.IS_UPLOAD_RESULT isUploadResult, api.PROJ_NAME projName, aipp.AUDIT_FLAG purchaseAuditFlag, aipp.AUDIT_FLAG, /*(CASE aipp.AUDIT_FLAG WHEN '0' THEN '未提交' WHEN '1' THEN '服务中' WHEN '2' THEN '服务完成' WHEN '3' THEN '服务中止' WHEN '4' THEN '审核中' WHEN '5' THEN '退回' WHEN '6' THEN '报名中' WHEN '7' THEN '选取中' WHEN '8' THEN '选取开始' WHEN '9' THEN '已选取' WHEN '10' THEN '无效' WHEN '11' THEN '待选取' WHEN '12' THEN '已过时' ELSE '' END) auditFlag,*/ aipp.BIDDING_TYPE biddingType,
( SELECT MAX(aibp.PRICE) FROM aea_im_bidding_price aibp WHERE aibp.IS_DELETE = '0' AND aibp.IS_CHOICE = '1' AND aibp.UNIT_BIDDING_ID = aiub.UNIT_BIDDING_ID ) realPrice, aic.PRICE contractPrice, aic.AUDIT_FLAG contractFlag, aic.CONTRACT_ID contractId, aic.IS_OWNER_UPLOAD isOwnerUpload, aic.SERVICE_START_TIME serviceStartTime, aic.SERVICE_END_TIME serviceEndTime,
aisr.AUDIT_FLAG resultFlag, aisr.SERVICE_RESULT_ID serviceResultId,
aipp.BASE_PRICE basePrice, aipp.HIGHEST_PRICE highestPrice, aiub.BIDDING_TIME biddingTime, aipp.CHOICE_IMUNIT_TIME choiceImunitTime FROM aea_im_unit_bidding aiub LEFT JOIN aea_im_proj_purchase aipp ON aiub.PROJ_PURCHASE_ID = aipp.PROJ_PURCHASE_ID LEFT JOIN aea_proj_info api ON api.PROJ_INFO_ID = aipp.PROJ_INFO_ID
LEFT JOIN aea_im_contract aic ON aic.UNIT_BIDDING_ID = aiub.UNIT_BIDDING_ID AND aic.IS_DELETE = '0'
LEFT JOIN aea_im_service_result aisr ON aisr.UNIT_BIDDING_ID = aiub.UNIT_BIDDING_ID
WHERE aiub.IS_CANCEL_SIGNUP = '0' AND aiub.IS_DELETE = '0' AND aipp.IS_DELETE = '0'
AND aiub.UNIT_INFO_ID = #{aeaImUnitBidding.unitInfoId}
AND aiub.UNIT_BIDDING_ID = #{aeaImUnitBidding.unitBiddingId}
AND aiub.IS_WON_BID = '1' AND aipp.AUDIT_FLAG != '10'
AND aiub.IS_WON_BID = '1' AND aiub.IS_UPLOAD_CONTRACT = '1' AND aipp.AUDIT_FLAG != '10'
AND aiub.IS_WON_BID = '1' AND aiub.IS_UPLOAD_CONTRACT = '1' AND aipp.AUDIT_FLAG in ('1', '2', '3')
AND api.PROJ_NAME LIKE concat('%',concat(#{aeaImUnitBidding.projName},'%'))
AND aipp.BIDDING_TYPE = #{aeaImUnitBidding.biddingType}
AND aipp.AUDIT_FLAG = #{aeaImUnitBidding.auditFlag}
and aiub.IS_WON_BID=#{aeaImUnitBidding.isWonBid}
AND aiub.IS_UPLOAD_CONTRACT = #{aeaImUnitBidding.isUploadContract}
and aipp.AUDIT_FLAG not in('0','4','5','10')

mapper.update:
update aea_im_unit_bidding aiub set aiub.IS_WON_BID='1' ,aiub.BIDDING_TIME=now()
where aiub.UNIT_BIDDING_ID=#{unitBiddingId}

    and aiub.PROJ_PURCHASE_ID=#{projPurchaseId}
