mapper.select:
select
, aib.ITEM_NAME itemName, asf.FORM_NAME formName, apsi.SUB_FORM_ID subFormId from AEA_PAR_FRONT_ITEMFORM apfi left join aea_par_stage_item apsi on apfi.STAGE_ITEM_ID = apsi.STAGE_ITEM_ID left join aea_item_basic aib on aib.ITEM_VER_ID = apsi.ITEM_VER_ID left join act_sto_form asf on asf.FORM_ID = apsi.SUB_FORM_ID
apfi.IS_ACTIVE = '1'
AND apfi.STAGE_ID = #{stageId}
AND apfi.ROOT_ORG_ID = #{rootOrgId}
order by apfi.SORT_NO

select

from AEA_PAR_FRONT_ITEMFORM


        AND FRONT_ITEMFORM_ID = #{frontItemformId}


        AND STAGE_ID = #{stageId}


        AND STAGE_ITEM_ID = #{stageItemId}


        AND SORT_NO = #{sortNo}


        AND IS_ACTIVE = #{isActive}


        AND FRONT_ITEMFORM_MEMO = #{frontItemformMemo}


        AND CREATER = #{creater}


        AND CREATE_TIME = #{createTime}


        AND MODIFIER = #{modifier}


        AND MODIFY_TIME = #{modifyTime}


        AND ROOT_ORG_ID = #{rootOrgId}

select


,
aib.ITEM_NAME itemName,
asf.FORM_NAME formName
from AEA_PAR_FRONT_ITEMFORM apfi left join aea_par_stage_item apsi on apfi.STAGE_ITEM_ID = apsi.STAGE_ITEM_ID
left join aea_item_basic aib on aib.ITEM_VER_ID = apsi.ITEM_VER_ID
left join act_sto_form asf on asf.FORM_ID = apsi.SUB_FORM_ID


        AND apfi.FRONT_ITEMFORM_ID = #{frontItemformId}


        AND apfi.STAGE_ID = #{stageId}


        AND apfi.STAGE_ITEM_ID = #{stageItemId}


        AND apfi.SORT_NO = #{sortNo}


        AND apfi.IS_ACTIVE = #{isActive}


        AND apfi.FRONT_ITEMFORM_MEMO = #{frontItemformMemo}


        AND CREATER = #{creater}


        AND apfi.CREATE_TIME = #{createTime}


        AND apfi.MODIFIER = #{modifier}


        AND apfi.MODIFY_TIME = #{modifyTime}


        AND apfi.ROOT_ORG_ID = #{rootOrgId}


        AND(
        aib.ITEM_NAME LIKE CONCAT(CONCAT('%',#{keyword}),'%')
        OR aib.ITEM_CODE LIKE CONCAT(CONCAT('%',#{keyword}),'%')
        OR aib.SSCODE LIKE CONCAT(CONCAT('%',#{keyword}),'%')
        OR asf.FORM_NAME LIKE CONCAT(CONCAT('%',#{keyword}),'%')
        OR asf.FORM_CODE LIKE CONCAT(CONCAT('%',#{keyword}),'%')
        )



order by apfi.SORT_NO

select apsi.STAGE_ITEM_ID stageItemId, aib.ITEM_NAME itemName, asf.FORM_NAME formName from aea_par_stage_item apsi inner join aea_item_basic aib on aib.ITEM_VER_ID = apsi.ITEM_VER_ID inner join act_sto_form asf on asf.FORM_ID = apsi.SUB_FORM_ID
AND apsi.STAGE_ID = #{stageId}
AND( aib.ITEM_NAME LIKE CONCAT(CONCAT('%',#{keyword}),'%') OR aib.ITEM_CODE LIKE CONCAT(CONCAT('%',#{keyword}),'%') OR aib.SSCODE LIKE CONCAT(CONCAT('%',#{keyword}),'%') OR asf.FORM_NAME LIKE CONCAT(CONCAT('%',#{keyword}),'%') OR asf.FORM_CODE LIKE CONCAT(CONCAT('%',#{keyword}),'%') )
and apsi.STAGE_ITEM_ID not in ( select apfi.STAGE_ITEM_ID from AEA_PAR_FRONT_ITEMFORM apfi
AND apfi.STAGE_ID = #{stageId}
and apfi.FRONT_ITEMFORM_ID != #{frontItemformId}
)
