mapper.select:
select

from AEA_PAR_IN


        AND IN_ID = #{inId}


        AND STAGE_ID = #{stageId}


        AND IS_OWNER = #{isOwner}


        AND IS_STATE_IN = #{isStateIn}


        AND PAR_STATE_ID = #{parStateId}


        AND FILE_TYPE = #{fileType}


        AND MAT_ID = #{matId}


        AND CERT_ID = #{certId}


        AND CREATER = #{creater}


        AND CREATE_TIME = #{createTime}


        AND MODIFIER = #{modifier}


        AND MODIFY_TIME = #{modifyTime}


        AND IS_DELETED = #{isDeleted}


        AND SORT_NO = #{sortNo}


        AND IS_COMMON = #{isCommon}


        AND ROOT_ORG_ID = #{rootOrgId}

    AND IS_DELETED = '0'

select a1.inId, a1.stageId, a1.isOwner, a1.fileType, a1.matId, a1.sortNo, a1.certId, a1.aeaMatCertName, a1.aeaMatCertCode from( select parIn.IN_ID inId, parIn.STAGE_ID stageId, parIn.IS_OWNER isOwner, parIn.IS_STATE_IN isStateIn, parIn.PAR_STATE_ID parStateId, parIn.FILE_TYPE fileType, parIn.MAT_ID matId, parIn.SORT_NO sortNo, parIn.CERT_ID certId, parIn.CREATER creater, parIn.CREATE_TIME createTime, parIn.MODIFIER modifier, parIn.MODIFY_TIME modifyTime, parIn.IS_DELETED isDeleted, mat.MAT_NAME as aeaMatCertName, mat.MAT_CODE as aeaMatCertCode from AEA_PAR_IN parIn, AEA_ITEM_MAT mat
and parIn.MAT_ID = mat.MAT_ID and parIn.ROOT_ORG_ID = mat.ROOT_ORG_ID and parIn.ROOT_ORG_ID = #{rootOrgId} and parIn.FILE_TYPE = 'mat' and parIn.IS_DELETED = '0' and parIn.STAGE_ID = #{stageId} and mat.IS_DELETED = '0'
and parIn.IS_STATE_IN = #{isStateIn}
and parIn.PAR_STATE_ID = #{parStateId}
and ( mat.MAT_NAME like CONCAT(CONCAT('%',#{keyword}),'%') OR mat.MAT_CODE like CONCAT(CONCAT('%',#{keyword}),'%') )
) a1 union all select b1.inId, b1.stageId, b1.isOwner, b1.fileType, b1.matId, b1.sortNo, b1.certId, b1.aeaMatCertName, b1.aeaMatCertCode from ( select parIn.IN_ID inId, parIn.STAGE_ID stageId, parIn.IS_OWNER isOwner, parIn.IS_STATE_IN isStateIn, parIn.PAR_STATE_ID parStateId, parIn.FILE_TYPE fileType, parIn.MAT_ID matId, parIn.SORT_NO sortNo, parIn.CERT_ID certId, parIn.CREATER creater, parIn.CREATE_TIME createTime, parIn.MODIFIER modifier, parIn.MODIFY_TIME modifyTime, parIn.IS_DELETED isDeleted, cert.cert_name as aeaMatCertName, cert.cert_code as aeaMatCertCode from AEA_PAR_IN parIn, AEA_CERT cert
and cert.CERT_ID = parIn.CERT_ID and cert.ROOT_ORG_ID = parIn.ROOT_ORG_ID and parIn.ROOT_ORG_ID = #{rootOrgId} and parIn.FILE_TYPE = 'cert' and parIn.IS_DELETED = '0' and parIn.STAGE_ID = #{stageId} and cert.IS_DELETED = '0'
and parIn.IS_STATE_IN = #{isStateIn}
and parIn.PAR_STATE_ID = #{parStateId}
and ( cert.cert_name like CONCAT(CONCAT('%',#{keyword}),'%') OR cert.cert_code like CONCAT(CONCAT('%',#{keyword}),'%') )
) b1

select
    max(SORT_NO)
from AEA_PAR_IN
where STAGE_ID = #{stageId}
  and IS_DELETED = '0'
  and ROOT_ORG_ID = #{rootOrgId}

mapper.update:
update AEA_PAR_IN
set PAR_STATE_ID = null
where IN_ID = #{inId}

update AEA_PAR_IN
set PAR_STATE_ID = #{parStateId}
where IN_ID = #{inId}

mapper.select:
select a1.inId, a1.stageId, a1.isOwner, a1.fileType, a1.matId, a1.certId, '' as formId, a1.sortNo, a1.aeaMatCertName, a1.aeaMatCertCode, a1.createTime from( select parIn.IN_ID inId, parIn.STAGE_ID stageId, parIn.IS_OWNER isOwner, parIn.IS_STATE_IN isStateIn, parIn.PAR_STATE_ID parStateId, parIn.FILE_TYPE fileType, parIn.MAT_ID matId, parIn.SORT_NO sortNo, parIn.CERT_ID certId, parIn.CREATER creater, parIn.CREATE_TIME createTime, parIn.MODIFIER modifier, parIn.MODIFY_TIME modifyTime, parIn.IS_DELETED isDeleted, mat.MAT_NAME as aeaMatCertName, mat.MAT_CODE as aeaMatCertCode from AEA_PAR_IN parIn, AEA_ITEM_MAT mat
and parIn.MAT_ID = mat.MAT_ID and parIn.ROOT_ORG_ID = mat.ROOT_ORG_ID and parIn.ROOT_ORG_ID = #{rootOrgId} and parIn.FILE_TYPE = 'mat' and parIn.IS_DELETED = '0' and parIn.STAGE_ID = #{stageId} and mat.IS_DELETED = '0'
and parIn.IS_STATE_IN = #{isStateIn}
and parIn.PAR_STATE_ID = #{parStateId}
and ( mat.MAT_NAME like CONCAT(CONCAT('%',#{keyword}),'%') or mat.MAT_CODE like CONCAT(CONCAT('%',#{keyword}),'%') )
) a1 union all select b1.inId, b1.stageId, b1.isOwner, b1.fileType, b1.matId, b1.certId, '' as formId, b1.sortNo, b1.aeaMatCertName, b1.aeaMatCertCode, b1.createTime from ( select parIn.IN_ID inId, parIn.STAGE_ID stageId, parIn.IS_OWNER isOwner, parIn.IS_STATE_IN isStateIn, parIn.PAR_STATE_ID parStateId, parIn.FILE_TYPE fileType, parIn.MAT_ID matId, parIn.SORT_NO sortNo, parIn.CERT_ID certId, parIn.CREATER creater, parIn.CREATE_TIME createTime, parIn.MODIFIER modifier, parIn.MODIFY_TIME modifyTime, parIn.IS_DELETED isDeleted, cert.cert_name as aeaMatCertName, cert.cert_code as aeaMatCertCode from AEA_PAR_IN parIn, AEA_cert cert
and parIn.CERT_ID = cert.cert_ID and parIn.ROOT_ORG_ID = cert.ROOT_ORG_ID and parIn.ROOT_ORG_ID = #{rootOrgId} and parIn.FILE_TYPE = 'cert' and parIn.IS_DELETED = '0' and parIn.STAGE_ID = #{stageId} and cert.IS_DELETED = '0'
and parIn.IS_STATE_IN = #{isStateIn}
and parIn.PAR_STATE_ID = #{parStateId}
and ( cert.cert_name like CONCAT(CONCAT('%',#{keyword}),'%') or cert.cert_code like CONCAT(CONCAT('%',#{keyword}),'%') )
) b1 union all select c1.stateFormId as inId, c1.parStageId as stageId, '1' as isOwner, 'form' as fileType, '' as matId, '' as certId, c1.formId as formId, c1.sortNo as sortNo, c1.formName as aeaMatCertName, c1.formCode as aeaMatCertCode, c1.createTime as createTime from ( select stateForm.STATE_FORM_ID as stateFormId, stateForm.PAR_STATE_ID as parStateId, stateForm.SORT_NO as sortNo, stateForm.PAR_STAGE_ID as parStageId, stateForm.IS_STATE_FORM as isStateForm, stateForm.CREATE_TIME as createTime, asfrom.FORM_ID as formId, asfrom.FORM_ORG_ID as formOrgId, asfrom.FORM_PROPERTY as formProperty, asfrom.FORM_CODE as formCode, asfrom.FORM_NAME as formName, asfrom.IS_INNER_FORM as isInnerForm, asfrom.FORM_LOAD_URL as formLoadUrl from AEA_PAR_STATE_FORM stateForm, ACT_STO_FORM asfrom
and stateForm.FORM_ID = asfrom.FORM_ID and stateForm.PAR_STAGE_ID = #{stageId}
and stateForm.IS_STATE_FORM = #{isStateIn}
and stateForm.PAR_STATE_ID = #{parStateId}
and ( asfrom.FORM_NAME like CONCAT(CONCAT('%',#{keyword}),'%') or asfrom.FORM_CODE like CONCAT(CONCAT('%',#{keyword}),'%') )
)c1 order by sortNo asc,createTime asc

select parIn.IN_ID inId, parIn.STAGE_ID stageId, parIn.IS_OWNER isOwner, parIn.IS_STATE_IN isStateIn, parIn.PAR_STATE_ID parStateId, parIn.FILE_TYPE fileType, parIn.MAT_ID matId, parIn.CERT_ID certId, parIn.CREATER creater, parIn.CREATE_TIME createTime, parIn.MODIFIER modifier, parIn.MODIFY_TIME modifyTime, parIn.IS_DELETED isDeleted, mat.MAT_NAME as aeaMatCertName, mat.MAT_CODE as aeaMatCertCode from AEA_PAR_IN parIn, AEA_ITEM_MAT mat
and mat.MAT_ID = parIn.MAT_ID and parIn.ROOT_ORG_ID = mat.ROOT_ORG_ID and parIn.ROOT_ORG_ID = #{rootOrgId}
and parIn.STAGE_ID = #{stageId}
and parIn.IS_STATE_IN = #{isStateIn}
and parIn.PAR_STATE_ID = #{parStateId}
and mat.IS_COMMON = #{isCommon}
and parIn.FILE_TYPE = 'mat' and parIn.IS_DELETED = '0' and mat.IS_DELETED = '0'

select

from aea_par_in where in_id in(
select IN_ID from aea_par_stage_item_in where STAGE_ITEM_ID=
(select STAGE_ITEM_ID from aea_par_stage_item where item_ver_id=#{itemVerId} and stage_id=#{stageId} )
) and ROOT_ORG_ID=#{rootOrgId}

SELECT api.* from aea_par_in api
where ((api.IS_STATE_IN ='0' or api.IS_STATE_IN is null) or
api.PAR_STATE_ID in (
    select ahps.EXEC_STATE_ID from aea_hi_par_stateinst ahps
    where ahps.STAGEINST_ID=#{stageinstId}
    AND ahps.ROOT_ORG_ID=#{rootOrgId}
))
and api.STAGE_ID=#{stageId} and api.ROOT_ORG_ID=#{rootOrgId}
