mapper.select:
   select



   from AEA_PAR_THEME apt

       and    apt.IS_DELETED = '0'

       and    apt.THEME_ID = #{themeId}


       and    apt.THEME_NAME = #{themeName}


       and    apt.THEME_CODE = #{themeCode}


       and    apt.THEME_TYPE = #{themeType}


       and    theme.DUE_NUM = #{dueNum}


       and    apt.DUE_UNIT = #{dueUnit}


       and    apt.COMPLAIN_PHONE = #{complainPhone}


       and    apt.HOTLINE_PHONE = #{hotlinePhone}


       and    apt.HandLE_ADDRESS = #{handleAddress}


       and    apt.HandLE_TIME = #{handleTime}


       and    apt.SORT_NO = #{sortNo}


       and    apt.IS_ACTIVE = #{isActive}


       and    apt.THEME_MEMO = #{themeMemo}


       and    apt.CREATER = #{creater}


       and    apt.CREATE_TIME = #{createTime}


       and    apt.MODIFIER = #{modifier}


       and    apt.MODIFY_TIME = #{modifyTime}


       AND    apt.THEME_ICON_CSS = #{themeIconCss}


       AND    apt.SMALL_IMG_PATH = #{smallImgPath}


       AND    apt.MIDDLE_IMG_PATH = #{middleImgPath}


       AND    apt.BIG_IMG_PATH = #{bigImgPath}


       AND    apt.HUGE_IMG_PATH = #{hugeImgPath}


       AND    apt.IS_IMG_ICON = #{isImgIcon}


       and    apt.IS_MAINLINE  = #{isMainline}


       and    apt.MAINLINE_ALIAS = #{mainlineAlias}


       and    apt.IS_AUXILINE  = #{isAuxiline}


       and    apt.AUXILINE_ALIAS  = #{auxilineAlias}


       and    apt.IS_TECHSPECTLINE  = #{isTechspectline}


       and    apt.TECHSPECTLINE_ALIAS  = #{techspectlineAlias}


       and    apt.GJBZSPLCLX = #{gjbzsplclx}


       and    apt.IS_ONLINE_SB = #{isOnlineSb}


       and    apt.ROOT_ORG_ID = #{rootOrgId}


       and (
           apt.THEME_NAME like CONCAT(CONCAT('%',#{keyword}),'%') or
           apt.THEME_MEMO like CONCAT(CONCAT('%',#{keyword}),'%')
       )


order by apt.THEME_TYPE asc, apt.SORT_NO asc, apt.CREATE_TIME asc

select distinct aea_123.* from ( select
from AEA_PAR_THEME apt
and apt.IS_DELETED = '0'
and apt.THEME_ID = #{themeId}
and apt.THEME_NAME = #{themeName}
and apt.THEME_CODE = #{themeCode}
and apt.THEME_TYPE = #{themeType}
and apt.IS_ACTIVE = #{isActive}
and apt.THEME_MEMO = #{themeMemo}
and apt.IS_ONLINE_SB = #{isOnlineSb}
and apt.ROOT_ORG_ID = #{rootOrgId}
and ( apt.THEME_NAME like CONCAT(CONCAT('%',#{keyword}),'%') )
union all
SELECT
FROM aea_par_theme apt LEFT JOIN ( SELECT aptv.THEME_VER_ID, aptv.VER_NUM, aptv.THEME_ID FROM aea_par_theme_ver aptv WHERE EXISTS ( SELECT * FROM aea_par_theme_seq apts WHERE apts.MAX_NUM = aptv.VER_NUM AND apts.THEME_ID = aptv.THEME_ID ) AND aptv.IS_PUBLISH IN ('1', '2') ) temp1 ON temp1.THEME_ID = apt.THEME_ID LEFT JOIN aea_par_stage aps ON aps.THEME_VER_ID = temp1.THEME_VER_ID WHERE apt.IS_ACTIVE = '1' AND apt.IS_DELETED = '0'
and apt.IS_ONLINE_SB = #{isOnlineSb}
AND aps.STAGE_NAME like CONCAT(CONCAT('%',#{keyword}),'%') AND aps.IS_DELETED = '0' ) aea_123 order by aea_123.themeType asc, aea_123.sortNo asc, aea_123.createTime asc

select , temp1.THEME_VER_ID themeVerId, temp1.VER_NUM verNum from aea_par_theme apt left join (select aptv.THEME_VER_ID, aptv.VER_NUM, aptv.THEME_ID from aea_par_theme_ver aptv where EXISTS(select * from aea_par_theme_seq apts where apts.MAX_NUM = aptv.VER_NUM and apts.THEME_ID = aptv.THEME_ID) and aptv.IS_PUBLISH in ('1', '2')) temp1 on temp1.THEME_ID = apt.THEME_ID where apt.IS_ACTIVE = '1' and IS_DELETED = '0'
and apt.THEME_TYPE = #{themeType}
and apt.THEME_ID = #{themeId}

select



from  AEA_PAR_THEME  theme

    and theme.ROOT_ORG_ID = #{rootOrgId}
    and theme.THEME_ID in (
        select distinct THEME_ID
        from AEA_PAR_THEME_VER themeVer
        where 1=1
        and themeVer.ROOT_ORG_ID = #{rootOrgId}
        and themeVer.IS_DELETED = '0'
        and themeVer.IS_PUBLISH in ('0', '1', '2')
        and themeVer.THEME_VER_ID in (
            select distinct THEME_VER_ID
            from aea_par_stage
            where STAGE_ID in (
                select distinct STAGE_ID
                from aea_par_stage_item
                where ITEM_ID = #{itemId}
                  and ITEM_VER_ID = #{itemVerId}
                  and ROOT_ORG_ID = #{rootOrgId}
            )
            and ROOT_ORG_ID = #{rootOrgId}
        )
    )
    and theme.IS_DELETED = '0'

order by theme.THEME_TYPE asc, theme.SORT_NO asc, theme.CREATE_TIME asc

select
, aptv2.THEME_VER_ID themeVerId, temp1.maxNum verNum from aea_par_theme apt join aea_par_theme_ver aptv2 on aptv2.theme_id=apt.theme_id join ( select aptv.theme_id, max(aptv.VER_NUM) maxNum from aea_par_theme_ver aptv where aptv.IS_PUBLISH in ('1', '2') and aptv.ROOT_ORG_ID=#{rootOrgId} and aptv.IS_DELETED = '0' group by aptv.theme_id ) temp1 on temp1.theme_id=aptv2.theme_id and temp1.maxNum=aptv2.VER_NUM where apt.IS_ACTIVE = '1' and apt.IS_DELETED = '0' and apt.root_org_id=#{rootOrgId}
and apt.THEME_TYPE = #{themeType}

select
from aea_par_theme apt join aea_par_theme_ver aptv on apt.theme_id=aptv.theme_id join (select aptv2.theme_id, max(aptv2.VER_NUM) maxNum from aea_par_theme_ver aptv2 where aptv2.IS_PUBLISH in ('1', '2') and aptv2.ROOT_ORG_ID=#{rootOrgId} and aptv2.IS_DELETED = '0' group by aptv2.theme_id) maxVer on maxVer.theme_id=aptv.theme_id and maxVer.maxNum=aptv.VER_NUM
and apt.IS_AUXILINE='1'
and apt.IS_AUXILINE='1'
and apt.theme_id=#{themeId}
and EXISTS( select 1 from aea_par_stage aps where aps.theme_ver_id=aptv.theme_ver_id and aps.is_deleted='0'
and is_node='1'
and is_node='2'
and DYGJBZFXFW = #{themeCategory}
)
