mapper.select:
select

from    AEA_PROJ_APPLY_AGENT


        AND    APPLY_AGENT_ID = #{applyAgentId}


        AND    AGENT_CODE = #{agentCode}


        AND    PROJ_INFO_ID = #{projInfoId}


        AND    UNIT_INFO_ID = #{unitInfoId}


        AND    APPLY_USER_ID = #{applyUserId}


        AND    AGENT_STAGE_STATE = #{agentStageState}


        AND    AGENT_APPLY_STATE = #{agentApplyState}


        AND    WINDOW_ID = #{windowId}


        AND    AGENT_USER_ID = #{agentUserId}


        AND    AGENT_USER_NAME = #{agentUserName}


        AND    AGENT_USER_MOBILE = #{agentUserMobile}


        AND    START_TIME = #{startTime}


        AND    END_TIME = #{endTime}


        AND    CREATER = #{creater}


        AND    CREATE_TIME = #{createTime}


        AND    MODIFIER = #{modifier}


        AND    MODIFY_TIME = #{modifyTime}


        AND    ROOT_ORG_ID = #{rootOrgId}


order by START_TIME desc

select


,
t.LOCAL_CODE as localCode,
t.GCBM as gcbm,
t.PROJ_NAME as projName,
t.THEME_NAME as themeName
from    AEA_PROJ_APPLY_AGENT agent
left join (
  select proj.PROJ_INFO_ID,proj.LOCAL_CODE,proj.GCBM,proj.PROJ_NAME,theme.THEME_NAME,theme.THEME_ID
  from aea_proj_info proj
  left join aea_par_theme theme on proj.THEME_ID = theme.THEME_ID
) t on agent.PROJ_INFO_ID = t.PROJ_INFO_ID



             AND to_char(agent.START_TIME , 'yyyy-mm-dd')>=  #{minStartTime}   


             AND to_char(agent.START_TIME , 'yyyy-mm-dd')<=  #{maxStartTime}   




             AND DATE_FORMAT(agent.START_TIME , '%Y-%m-%d')>=  #{minStartTime}   


             AND DATE_FORMAT(agent.START_TIME , '%Y-%m-%d')<=  #{maxStartTime}   



        AND    t.THEME_ID = #{themeId}


        AND    agent.APPLY_AGENT_ID = #{applyAgentId}


        AND    agent.AGENT_CODE = #{agentCode}


        AND    agent.PROJ_INFO_ID = #{projInfoId}


        AND    agent.UNIT_INFO_ID = #{unitInfoId}


        AND    agent.APPLY_USER_ID = #{applyUserId}


        AND    agent.AGENT_STAGE_STATE = #{agentStageState}


        AND    agent.AGENT_APPLY_STATE = #{agentApplyState}


        AND    agent.WINDOW_ID = #{windowId}


        AND    agent.WINDOW_ID in

            #{windowId}



        AND    agent.AGENT_USER_ID = #{agentUserId}


        AND    agent.AGENT_USER_NAME = #{agentUserName}


        AND    agent.AGENT_USER_MOBILE = #{agentUserMobile}


        AND    agent.START_TIME = #{startTime}


        AND    agent.END_TIME = #{endTime}


        AND    agent.CREATER = #{creater}


        AND    agent.CREATE_TIME = #{createTime}


        AND    agent.MODIFIER = #{modifier}


        AND    agent.MODIFY_TIME = #{modifyTime}


        AND    agent.ROOT_ORG_ID = #{rootOrgId}


        AND (
        t.PROJ_NAME LIKE CONCAT(CONCAT('%',#{keyword}),'%')
        OR t.LOCAL_CODE LIKE CONCAT(CONCAT('%',#{keyword}),'%')
        OR t.GCBM LIKE CONCAT(CONCAT('%',#{keyword}),'%')
        OR agent.AGENT_CODE LIKE CONCAT(CONCAT('%',#{keyword}),'%')
        )
