mapper.select:
select

from AEA_SERVICE_WINDOW window

    and window.WINDOW_ID not in (
        select distinct windowRel.WINDOW_ID from AEA_SERVICE_WINDOW_REL windowRel


                and windowRel.TABLE_NAME = #{tableName}


                and windowRel.PK_NAME = #{pkName}


                and windowRel.RECORD_ID = #{recordId}


    )

        and  window.WINDOW_ID = #{windowId}


        and  window.WINDOW_NAME = #{windowName}


        and   window.REGION_ID = #{regionId}


        and   window.ORG_ID = #{orgId}


        and  window.LINK_PHONE = #{linkPhone}


        and  window.WORK_TIME = #{workTime}


        and  window.WINDOW_ADDRESS = #{windowAddress}


        and  window.TRAFFIC_GUIDE = #{trafficGuide}


        and  window.WINDOW_MEMO = #{windowMemo}


        and  window.MAP_URL = #{mapUrl}


        and  window.MAP_ATT = #{mapAtt}


        and window.SORT_NO = #{sortNo}


        and window.IS_ACTIVE = #{isActive}


        and window.WINDOW_TYPE = #{windowType}


        and window.IS_PUBLIC = #{isPublic}


        and window.IS_ALL_ITEM = #{isAllItem}


        and window.IS_ALL_STAGE = #{isAllStage}


        and window.ROOT_ORG_ID = #{rootOrgId}


        and window.REGION_RANGE = #{regionRange}


        AND window.STAGE_ITEM_FILTER = #{stageItemFilter}


        and  (
               window.WINDOW_NAME like CONCAT(CONCAT('%',#{keyword}),'%')
            or window.WINDOW_ADDRESS like CONCAT(CONCAT('%',#{keyword}),'%')
            or window.WINDOW_MEMO like CONCAT(CONCAT('%',#{keyword}),'%')
            or window.LINK_PHONE like CONCAT(CONCAT('%',#{keyword}),'%')
        )

select
from AEA_SERVICE_WINDOW window where 1 = 1
and ROOT_ORG_ID = #{rootOrgId}

select

from AEA_SERVICE_WINDOW window
WHERE window.REGION_ID = #{regionId}

    and ROOT_ORG_ID = #{rootOrgId}

select
    ,
    org.org_name orgName,
    region.region_name regionName
from AEA_SERVICE_WINDOW window
left join opu_om_org org on org.ORG_ID = window.ORG_ID
left join bsc_dic_region region on region.REGION_ID = window.REGION_ID


        and  window.WINDOW_ID = #{windowId}


        and  window.WINDOW_NAME = #{windowName}


        and  window.REGION_ID = #{regionId}


        and  window.ORG_ID = #{orgId}


        and  window.LINK_PHONE = #{linkPhone}


        and  window.WORK_TIME = #{workTime}


        and  window.WINDOW_ADDRESS = #{windowAddress}


        and  window.TRAFFIC_GUIDE = #{trafficGuide}


        and  window.WINDOW_MEMO = #{windowMemo}


        and  window.MAP_URL = #{mapUrl}


        and  window.MAP_ATT = #{mapAtt}


        and window.SORT_NO = #{sortNo}


        and window.IS_ACTIVE = #{isActive}


        and window.WINDOW_TYPE = #{windowType}


        and window.IS_PUBLIC = #{isPublic}


        and window.IS_ALL_ITEM = #{isAllItem}


        and window.IS_ALL_STAGE = #{isAllStage}


        and window.ROOT_ORG_ID = #{rootOrgId}


        and window.REGION_RANGE = #{regionRange}


        AND window.STAGE_ITEM_FILTER = #{stageItemFilter}


        and (
        window.WINDOW_NAME LIKE CONCAT(CONCAT('%',#{keyword}),'%')
        or org.org_name LIKE CONCAT(CONCAT('%',#{keyword}),'%')
        or region.region_name LIKE CONCAT(CONCAT('%',#{keyword}),'%')
        )


order by window.SORT_NO desc, region.REGIONAL_LEVEL asc, org.ORG_LEVEL asc, org.ORG_SORT_NO asc

select

from AEA_SERVICE_WINDOW window

    and window.WINDOW_ID not in (
    select distinct WIND_ID
    from AEA_SERVICE_WINDOW_ITEM
    where ITEM_VER_ID = #{itemVerId}
    )

        and  window.WINDOW_ID = #{windowId}


        and  window.WINDOW_NAME = #{windowName}


        and   window.REGION_ID = #{regionId}


        and   window.ORG_ID = #{orgId}


        and  window.LINK_PHONE = #{linkPhone}


        and  window.WORK_TIME = #{workTime}


        and  window.WINDOW_ADDRESS = #{windowAddress}


        and  window.TRAFFIC_GUIDE = #{trafficGuide}


        and  window.WINDOW_MEMO = #{windowMemo}


        and  window.MAP_URL = #{mapUrl}


        and  window.MAP_ATT = #{mapAtt}


        and window.SORT_NO = #{sortNo}


        and window.IS_ACTIVE = #{isActive}


        and window.WINDOW_TYPE = #{windowType}


        and window.IS_PUBLIC = #{isPublic}


        and window.IS_ALL_ITEM = #{isAllItem}


        and window.IS_ALL_STAGE = #{isAllStage}


        and window.ROOT_ORG_ID = #{rootOrgId}


        and window.REGION_RANGE = #{regionRange}


        AND window.STAGE_ITEM_FILTER = #{stageItemFilter}


        and  (
        window.WINDOW_NAME like CONCAT(CONCAT('%',#{keyword}),'%')
        or window.WINDOW_ADDRESS like CONCAT(CONCAT('%',#{keyword}),'%')
        or window.WINDOW_MEMO like CONCAT(CONCAT('%',#{keyword}),'%')
        or window.LINK_PHONE like CONCAT(CONCAT('%',#{keyword}),'%')
        )

select

from AEA_SERVICE_WINDOW window

    and window.WINDOW_ID not in (
    select distinct WIND_ID
    from AEA_SERVICE_WINDOW_STAGE
    where STAGE_ID = #{stageId}
    )

        and  window.WINDOW_ID = #{windowId}


        and  window.WINDOW_NAME = #{windowName}


        and   window.REGION_ID = #{regionId}


        and   window.ORG_ID = #{orgId}


        and  window.LINK_PHONE = #{linkPhone}


        and  window.WORK_TIME = #{workTime}


        and  window.WINDOW_ADDRESS = #{windowAddress}


        and  window.TRAFFIC_GUIDE = #{trafficGuide}


        and  window.WINDOW_MEMO = #{windowMemo}


        and  window.MAP_URL = #{mapUrl}


        and  window.MAP_ATT = #{mapAtt}


        and window.SORT_NO = #{sortNo}


        and window.IS_ACTIVE = #{isActive}


        and window.WINDOW_TYPE = #{windowType}


        and window.IS_PUBLIC = #{isPublic}


        and window.IS_ALL_ITEM = #{isAllItem}


        and window.IS_ALL_STAGE = #{isAllStage}


        and window.ROOT_ORG_ID = #{rootOrgId}


        and window.REGION_RANGE = #{regionRange}


        AND window.STAGE_ITEM_FILTER = #{stageItemFilter}


        and  (
        window.WINDOW_NAME like CONCAT(CONCAT('%',#{keyword}),'%')
        or window.WINDOW_ADDRESS like CONCAT(CONCAT('%',#{keyword}),'%')
        or window.WINDOW_MEMO like CONCAT(CONCAT('%',#{keyword}),'%')
        or window.LINK_PHONE like CONCAT(CONCAT('%',#{keyword}),'%')
        )
