mapper.select:
select

from    AEA_SOLICIT_ITEM


    and    SOLICIT_ITEM_ID = #{solicitItemId}


    and    ITEM_ID = #{itemId}


    and    ITEM_VER_ID = #{itemVerId}


    and    BUS_TYPE = #{busType}


    and    SOLICIT_TYPE = #{solicitType}


    and    CREATER = #{creater}


    and    CREATE_TIME = #{createTime}


    and    MODIFIER = #{modifier}


    and    MODIFY_TIME = #{modifyTime}


    and    ROOT_ORG_ID = #{rootOrgId}

select
, item.ITEM_BASIC_ID as itemBasicId, item.ITEM_NAME as itemName, item.ITEM_CODE as itemCode, item.ITEM_NATURE as itemNature, item.IS_CATALOG as isCatalog, item.GUIDE_ORG_NAME as guideOrgName, item.ORG_NAME as orgName from AEA_SOLICIT_ITEM soliItem left join ( select aib.ITEM_ID, aib.ITEM_VER_ID, aib.ITEM_BASIC_ID, aib.ITEM_NAME, aib.ITEM_CODE, aib.ITEM_NATURE, aib.DUE_NUM, aib.BJ_TYPE, aib.IS_CATALOG, aib.GUIDE_ORG_NAME, org.ORG_NAME from AEA_ITEM_BASIC aib left join OPU_OM_ORG org on org.ORG_ID = aib.ORG_ID and org.ORG_DELETED = '0' )item on item.ITEM_ID = soliItem.ITEM_ID and item.ITEM_VER_ID = soliItem.ITEM_VER_ID
and soliItem.SOLICIT_ITEM_ID = #{solicitItemId}
and soliItem.ITEM_ID = #{itemId}
and soliItem.ITEM_VER_ID = #{itemVerId}
and soliItem.BUS_TYPE = #{busType}
and soliItem.SOLICIT_TYPE = #{solicitType}
and soliItem.ROOT_ORG_ID = #{rootOrgId}
and ( item.ITEM_NAME like concat('%',concat(#{keyword},'%')) or item.ITEM_CODE like concat('%',concat(#{keyword},'%')) or item.GUIDE_ORG_NAME like concat('%',concat(#{keyword},'%')) or item.ORG_NAME like concat('%',concat(#{keyword},'%')) )
order by item.ITEM_ID desc, soliItem.CREATE_TIME desc

select
, asiu.USER_ID userId from AEA_SOLICIT_ITEM asi join aea_solicit_item_user asiu on asiu.SOLICIT_ITEM_ID=asi.SOLICIT_ITEM_ID where asi.ITEM_VER_ID in
#{itemVerId}
