mapper.select:
select

from    AEA_SOLICIT_ITEM_USER


    and    ITEM_USER_ID = #{itemUserId}


    and    SOLICIT_ITEM_ID = #{solicitItemId}


    and    USER_ID = #{userId}


    and    SORT_NO = #{sortNo}


    and    IS_ACTIVE = #{isActive}


    and    CREATER = #{creater}


    and    CREATE_TIME = #{createTime}


    and    MODIFIER = #{modifier}


    and    MODIFY_TIME = #{modifyTime}

select
, omuser.USER_NAME as userName, omuser.LOGIN_NAME as loginName, omuser.USER_SEX as userSex, userInfo.USER_MOBILE as userMobile from AEA_SOLICIT_ITEM_USER asou left join OPU_OM_USER omuser on omuser.USER_ID = asou.USER_ID left join OPU_OM_USER_INFO userInfo on omuser.USER_ID = userInfo.USER_ID
and asou.ITEM_USER_ID = #{itemUserId}
and asou.SOLICIT_ITEM_ID = #{solicitItemId}
and asou.USER_ID = #{userId}
and asou.SORT_NO = #{sortNo}
and asou.IS_ACTIVE = #{isActive}
and ( omuser.USER_NAME like concat('%', concat(#{keyword}, '%')) or omuser.LOGIN_NAME like concat('%', concat(#{keyword}, '%')) or omuser.USER_SEX like concat('%', concat(#{keyword}, '%')) or userInfo.USER_MOBILE like concat('%', concat(#{keyword}, '%')) )
order by asou.SORT_NO asc, asou.CREATE_TIME asc

select

from  AEA_SOLICIT_ITEM_USER

    IS_ACTIVE = '1'
    and SOLICIT_ITEM_ID in (
        select
           SOLICIT_ITEM_ID
        from AEA_SOLICIT_ITEM

            and ITEM_VER_ID = #{itemVerId}
            and ROOT_ORG_ID = #{rootOrgId}

                and BUS_TYPE = #{busType}


    )

order by SORT_NO desc, CREATE_TIME desc
