mapper.select:
select

from AEA_SOLICIT_ORG


    and    SOLICIT_ORG_ID = #{solicitOrgId}


    and    ORG_ID = #{orgId}


    and    BUS_TYPE = #{busType}


    and    SOLICIT_TYPE = #{solicitType}


    and    CREATER = #{creater}


    and    CREATE_TIME = #{createTime}


    and    MODIFIER = #{modifier}


    and    MODIFY_TIME = #{modifyTime}


    and    ROOT_ORG_ID = #{rootOrgId}

select


    ,
    org.ORG_NAME orgName,
    org.ORG_CODE orgCode
from AEA_SOLICIT_ORG soliOrg
left join OPU_OM_ORG org on ( org.ORG_ID = soliOrg.ORG_ID and org.ORG_DELETED = '0' )


    and    soliOrg.SOLICIT_ORG_ID = #{solicitOrgId}


    and    soliOrg.ORG_ID = #{orgId}


    and    soliOrg.BUS_TYPE = #{busType}


    and    soliOrg.SOLICIT_TYPE = #{solicitType}


    and    soliOrg.CREATER = #{creater}


    and    soliOrg.CREATE_TIME = #{createTime}


    and    soliOrg.MODIFIER = #{modifier}


    and    soliOrg.MODIFY_TIME = #{modifyTime}


    and    soliOrg.ROOT_ORG_ID = #{rootOrgId}


    and (
        org.ORG_NAME like concat('%', concat( #{keyword}, '%')) or
        org.ORG_CODE like concat('%', concat( #{keyword}, '%'))
    )
