mapper.select:
select



from AEA_SOLICIT_ORG_USER asou
left join AEA_SOLICIT_ORG aso on aso.SOLICIT_ORG_ID = asou.SOLICIT_ORG_ID
where asou.IS_ACTIVE = '1' and aso.ORG_ID = #{orgId}

    and aso.BUS_TYPE = #{busType}

select

from    AEA_SOLICIT_ORG_USER


    and    ORG_USER_ID = #{orgUserId}


    and    SOLICIT_ORG_ID = #{solicitOrgId}


    and    USER_ID = #{userId}


    and    SORT_NO = #{sortNo}


    and    IS_ACTIVE = #{isActive}


    and    CREATER = #{creater}


    and    CREATE_TIME = #{createTime}


    and    MODIFIER = #{modifier}


    and    MODIFY_TIME = #{modifyTime}


order by SORT_NO asc, CREATE_TIME asc

select
, omuser.USER_NAME as userName, omuser.LOGIN_NAME as loginName, omuser.USER_SEX as userSex, userInfo.USER_MOBILE as userMobile from AEA_SOLICIT_ORG_USER asou left join OPU_OM_USER omuser on omuser.USER_ID = asou.USER_ID left join OPU_OM_USER_INFO userInfo on omuser.USER_ID = userInfo.USER_ID
and asou.ORG_USER_ID = #{orgUserId}
and asou.SOLICIT_ORG_ID = #{solicitOrgId}
and asou.USER_ID = #{userId}
and asou.IS_ACTIVE = #{isActive}
and ( omuser.USER_NAME like concat('%', concat(#{keyword}, '%')) or omuser.LOGIN_NAME like concat('%', concat(#{keyword}, '%')) or omuser.USER_SEX like concat('%', concat(#{keyword}, '%')) or userInfo.USER_MOBILE like concat('%', concat(#{keyword}, '%')) )
order by asou.SORT_NO asc, asou.CREATE_TIME asc
