mapper.select:
select
,acr.IS_BLACK isBlack,acr.REDBLACK_ID redblackId,aup1.UNIT_PROJ_ID unitProjId from AEA_UNIT_INFO aui left join aea_unit_proj aup1 on aup1.UNIT_INFO_ID=aui.UNIT_INFO_ID left join aea_credit_redblack acr on acr.UNIT_INFO_ID=aui.UNIT_INFO_ID WHERE aui.UNIT_INFO_ID IN ( SELECT up.UNIT_INFO_ID FROM AEA_APPLYINST_UNIT_PROJ aup INNER JOIN AEA_UNIT_PROJ up ON up.UNIT_PROJ_ID = aup.UNIT_PROJ_ID WHERE aup.APPLYINST_ID = #{applyinstId} and up.is_deleted='0'
AND up.PROJ_INFO_ID = #{projInfoId}
AND up.IS_OWNER = #{isOwner}
)
AND aup1.PROJ_INFO_ID = #{projInfoId}
AND aui.IS_DELETED = '0' and aup1.is_deleted='0'

select

from AEA_UNIT_INFO
WHERE UNIT_INFO_ID IN (
SELECT UNIT_INFO_ID
FROM AEA_UNIT_PROJ
WHERE PROJ_INFO_ID = #{projInfoId}
AND IS_DELETED = '0'
)
AND UNIT_TYPE = #{unitType}
AND IS_DELETED = '0'
AND ROOT_ORG_ID=#{rootOrgId}

select
,aup.UNIT_TYPE unitProjUnitType,aup.LINKMAN_INFO_ID linkmanInfoId,aup.UNIT_PROJ_ID unitProjId from AEA_UNIT_INFO aui,AEA_UNIT_PROJ aup WHERE aui.UNIT_INFO_ID = aup.UNIT_INFO_ID AND aup.PROJ_INFO_ID = #{projInfoId}
AND aup.IS_OWNER = #{isOwner}
AND aui.IS_DELETED = '0' and aup.is_deleted='0'

select

from AEA_UNIT_INFO
WHERE LOGIN_NAME = #{loginName}
AND IS_DELETED = '0'
AND ROOT_ORG_ID=#{rootOrgId}

select

from AEA_UNIT_INFO


        AND UNIT_INFO_ID = #{unitInfoId}


        AND APPLICANT = #{applicant}


        AND IDTYPE = #{idtype}


        AND IDCARD = #{idcard}


        and indu_comm_reg_num = #{induCommRegNum}


        and organizational_code = #{organizationalCode}


        and unified_social_credit_code = #{unifiedSocialCreditCode}


        and TAXPAYER_NUM = #{taxpayerNum}


        and CREDIT_FLAG_NUM = #{creditFlagNum}


        AND CONTACT = #{contact}


        AND MOBILE = #{mobile}


        AND EMAIL = #{email}


        AND APPLICANT_DISTRICT = #{applicantDistrict}


        AND APPLICANT_DETAIL_SITE = #{applicantDetailSite}


        AND IDREPRESENTATIVE = #{idrepresentative}


        AND IDMOBILE = #{idmobile}


        AND IDNO = #{idno}


        AND IS_DELETED = #{isDeleted}


        AND IS_PRIMARY_UNIT = #{isPrimaryUnit}


        AND PARENT_ID = #{parentId}


        AND UNIT_INFO_SEQ = #{unitInfoSeq}


        AND LOGIN_NAME = #{loginName}


        AND UNIT_TYPE = #{unitType}


        AND LOGIN_PWD = #{loginPwd}


        AND MANAGEMENT_SCOPE = #{managementScope}


        AND REGISTERED_CAPITAL = #{registeredCapital}


        AND REGISTER_AUTHORITY = #{registerAuthority}


        AND UNIT_NATURE = #{unitNature}


        AND POSTAL_CODE = #{postalCode}


        AND IS_IM_UNIT = #{isImUnit}


        AND IMG_URL = #{imgUrl}


        AND IS_OWNER_UNIT = #{isOwnerUnit}


        AND (APPLICANT LIKE CONCAT(CONCAT('%',#{keyword}),'%'))


        AND ROOT_ORG_ID = #{rootOrgId}

    AND IS_DELETED = '0'

select

from AEA_UNIT_INFO aul
where 1=1
and AUDIT_FLAG is not null and AUDIT_FLAG !=''

    and APPLICANT LIKE CONCAT(CONCAT('%',#{applicant}),'%')


    and unified_social_credit_code LIKE CONCAT(CONCAT('%',#{unifiedSocialCreditCode}),'%')


    and IS_IM_UNIT = #{isImUnit}


    and IS_OWNER_UNIT = #{isOwnerUnit}


    and AUDIT_FLAG = #{auditFlag}


    AND ROOT_ORG_ID = #{rootOrgId}
