mapper.select:
select

from AEA_UNIT_PROJ
where PROJ_INFO_ID = #{projInfoId}
AND UNIT_INFO_ID = #{unitInfoId}
AND IS_OWNER = #{isOwner}
and is_deleted='0'

select

from AEA_UNIT_PROJ
where PROJ_INFO_ID IN

    #{projInfoId}

AND UNIT_INFO_ID IN

    #{unitInfoId}

AND IS_OWNER = #{isOwner}
and is_deleted='0'

select

from AEA_UNIT_PROJ

    is_deleted='0'

        AND UNIT_PROJ_ID = #{unitProjId}


        AND UNIT_INFO_ID = #{unitInfoId}


        AND IS_OWNER = #{isOwner}


        AND PROJ_INFO_ID = #{projInfoId}


        AND CREATER = #{creater}


        AND UNIT_TYPE = #{unitType}


        AND LINKMAN_INFO_ID = #{linkmanInfoId}

mapper.update:
update AEA_UNIT_PROJ
set is_deleted='1'
where PROJ_INFO_ID = #{projId}

    AND IS_OWNER = #{isOwner}

AND UNIT_INFO_ID IN

    #{unitInfoId}

update AEA_UNIT_PROJ
set is_deleted='1'
where PROJ_INFO_ID = #{projId}

mapper.select:
select ui.UNIT_INFO_ID unitInfoId, ui.APPLICANT applicant, ui.IDTYPE idtype, ui.IDCARD idcard, ui.CONTACT contact, ui.MOBILE mobile, ui.EMAIL email, ui.APPLICANT_DISTRICT applicantDistrict, ui.APPLICANT_DETAIL_SITE applicantDetailSite, ui.IDREPRESENTATIVE idrepresentative, ui.IDMOBILE idmobile, ui.IDNO idno, ui.IS_DELETED isDeleted, ui.CREATER creater, ui.CREATE_TIME createTime, ui.MODIFIER modifier, ui.MODIFY_TIME modifyTime, ui.IS_PRIMARY_UNIT isPrimaryUnit, ui.PARENT_ID parentId, ui.UNIT_INFO_SEQ unitInfoSeq, ui.LOGIN_NAME loginName, up.PROJ_INFO_ID projInfoId, up.UNIT_PROJ_ID unitProjId, up.UNIT_TYPE unitType, up.IS_OWNER isOwner from AEA_UNIT_PROJ up LEFT JOIN AEA_UNIT_INFO ui ON up.UNIT_INFO_ID = ui.UNIT_INFO_ID
AND ui.IS_DELETED = '0'
AND up.UNIT_PROJ_ID = #{unitProjId}
AND up.UNIT_INFO_ID = #{unitInfoId}
AND up.IS_OWNER = #{isOwner}
AND up.PROJ_INFO_ID = #{projInfoId}
AND up.UNIT_TYPE = #{unitType}
ORDER BY up.CREATE_TIME DESC

select

from AEA_UNIT_PROJ

    is_deleted='0'
    AND PROJ_INFO_ID = #{projInfoId}
    AND UNIT_INFO_ID = #{unitInfoId}
    AND UNIT_TYPE = #{unitType}
