SELECT * FROM ( SELECT ahap_123.APPLYINST_ID AS applyinstId, ahap_123.APPLYINST_STATE AS applyState, ahap_123.APPLYINST_CODE AS applyinstCode, temp1.PROJ_NAME AS projName, temp1.LOCAL_CODE AS localCode, temp1.GCBM AS gcbm, ahap_123.APPLYINST_SOURCE AS applySource, ali_123.LINKMAN_NAME AS linkmanName, CASE ahap_123.IS_SERIES_APPROVE WHEN '1' THEN '单项' ELSE '并联' END AS applyType, theme.THEME_NAME AS themeName, aps_123.STAGE_NAME AS stageName, ahap_123.CREATE_TIME AS applyTime, tinst.OVERDUE_TIME AS overdueTime, tinst.REMAINING_TIME AS remainingTime, tinst.INST_STATE AS instState, tinst.TIMERULE_UNIT AS timeruleUnit, aps_123.DUE_NUM AS dueNum, NULL AS itemName FROM aea_hi_applyinst ahap_123
INNER JOIN ( SELECT DISTINCT alog.APPLYINST_ID FROM aea_log_apply_state_hist alog
AND alog.OPS_WINDOW_ID = #{windowId}
AND alog.OPS_USER_ID = #{userId}
)wi ON wi.APPLYINST_ID = ahap_123.APPLYINST_ID
INNER JOIN ( SELECT ap.APPLYINST_ID AS APPLYINST_ID, GROUP_CONCAT(p.PROJ_NAME SEPARATOR ',') AS PROJ_NAME, GROUP_CONCAT(p.LOCAL_CODE SEPARATOR ',') AS LOCAL_CODE, GROUP_CONCAT(p.GCBM SEPARATOR ',') AS GCBM FROM aea_applyinst_proj ap LEFT JOIN aea_proj_info p ON ap.PROJ_INFO_ID = p.PROJ_INFO_ID
AND ptmp.REGIONALISM = #{region}
AND ptmp.PROJ_TYPE = #{projType}
AND ptmp.INVEST_TYPE = #{investCategory}
AND ptmp.INDUSTRY IN
#{item}
AND ptmp.PROJ_NATURE = #{buildNature}
AND ptmp.PROJ_LEVEL = #{projLevel}
GROUP BY ap.APPLYINST_ID ) temp1 ON temp1.APPLYINST_ID = ahap_123.APPLYINST_ID LEFT JOIN aea_linkman_info ali_123 ON ali_123.LINKMAN_INFO_ID = ahap_123.LINKMAN_INFO_ID LEFT JOIN aea_hi_par_stageinst ahs_123 ON ahs_123.APPLYINST_ID = ahap_123.APPLYINST_ID LEFT JOIN aea_par_stage aps_123 ON aps_123.STAGE_ID = ahs_123.STAGE_ID LEFT JOIN aea_par_theme_ver ver ON aps_123.THEME_VER_ID = ver.THEME_VER_ID LEFT JOIN aea_par_theme theme ON theme.THEME_ID = ver.THEME_ID LEFT JOIN ( SELECT asti.REMAINING_TIME, asti.INST_STATE, ahps.STAGEINST_ID, asti.TIMERULE_UNIT, asti.OVERDUE_TIME FROM act_sto_timerule_inst asti, act_sto_appinst asp, aea_hi_par_stageinst ahps WHERE asti.APP_FLOWDEF_ID = asp.APP_FLOWDEF_ID AND asti.PROC_INST_ID = asp.PROCINST_ID AND asp.APPINST_ID = ahps.APPINST_ID AND ahps.ROOT_ORG_ID = #{currentOrgId} AND asp.ROOT_ORG_ID = #{currentOrgId} AND asti.ORG_ID = #{currentOrgId} ) tinst ON tinst.STAGEINST_ID = ahs_123.STAGEINST_ID WHERE ahap_123.IS_SERIES_APPROVE = '0'
AND theme.THEME_ID = #{theme}
AND aps_123.STAGE_ID = #{approvalStage}
AND DATE_FORMAT(ahap_123.CREATE_TIME , '%Y-%m-%d')>= #{applyStartTime}
AND DATE_FORMAT(ahap_123.CREATE_TIME , '%Y-%m-%d')<= #{applyEndTime}
AND ahap_123.APPLYINST_SOURCE = #{applySource}
AND ahap_123.IS_SERIES_APPROVE = #{applyType}
AND ahap_123.APPLYINST_STATE = #{applyState}
AND tinst.INST_STATE = #{instState}
UNION SELECT ahap_123.APPLYINST_ID AS applyinstId, ahap_123.APPLYINST_STATE AS applyState, ahap_123.APPLYINST_CODE AS applyinstCode, temp1.PROJ_NAME AS projName, temp1.LOCAL_CODE AS localCode, temp1.GCBM AS gcbm, ahap_123.APPLYINST_SOURCE AS applySource, ali_123.LINKMAN_NAME AS linkmanName, CASE ahap_123.IS_SERIES_APPROVE WHEN '1' THEN '单项' ELSE '并联' END AS applyType, NULL AS THEME_NAME, NULL AS stageName, ahap_123.CREATE_TIME AS applyTime, tinst.OVERDUE_TIME AS overdueTime, tinst.REMAINING_TIME AS remainingTime, tinst.INST_STATE AS instState, tinst.TIMERULE_UNIT AS timeruleUnit, ai_123.DUE_NUM AS dueNum, ahi_123.ITEMINST_NAME AS itemName FROM aea_hi_applyinst ahap_123
INNER JOIN ( SELECT DISTINCT alog.APPLYINST_ID FROM aea_log_apply_state_hist alog
AND alog.OPS_WINDOW_ID = #{windowId}
AND alog.OPS_USER_ID = #{userId}
)wi ON wi.APPLYINST_ID = ahap_123.APPLYINST_ID
INNER JOIN ( SELECT ap.APPLYINST_ID AS APPLYINST_ID, GROUP_CONCAT(p.PROJ_NAME SEPARATOR ',') AS PROJ_NAME, GROUP_CONCAT(p.LOCAL_CODE SEPARATOR ',') AS LOCAL_CODE, GROUP_CONCAT(p.GCBM SEPARATOR ',') AS GCBM FROM aea_applyinst_proj ap LEFT JOIN aea_proj_info p ON ap.PROJ_INFO_ID = p.PROJ_INFO_ID
AND ptmp.REGIONALISM = #{region}
AND ptmp.PROJ_TYPE = #{projType}
AND ptmp.INVEST_TYPE = #{investCategory}
AND ptmp.INDUSTRY IN
#{item}
AND ptmp.PROJ_NATURE = #{buildNature}
AND ptmp.PROJ_LEVEL = #{projLevel}
GROUP BY ap.APPLYINST_ID ) temp1 ON temp1.APPLYINST_ID = ahap_123.APPLYINST_ID LEFT JOIN aea_linkman_info ali_123 ON ali_123.LINKMAN_INFO_ID = ahap_123.LINKMAN_INFO_ID LEFT JOIN aea_hi_seriesinst ahs_123 ON ahs_123.APPLYINST_ID = ahap_123.APPLYINST_ID LEFT JOIN aea_hi_iteminst ahi_123 ON ahi_123.SERIESINST_ID = ahs_123.SERIESINST_ID LEFT JOIN AEA_ITEM_BASIC ai_123 ON ahi_123.ITEM_ID = ai_123.ITEM_ID AND ahi_123.ITEM_VER_ID = ai_123.ITEM_VER_ID LEFT JOIN ( SELECT asti.INST_STATE, asti.REMAINING_TIME, ahi.ITEMINST_ID, asti.TIMERULE_UNIT, asti.OVERDUE_TIME FROM act_sto_timerule_inst asti, aea_hi_iteminst ahi WHERE asti.PROC_INST_ID = ahi.PROCINST_ID AND asti.ORG_ID = #{currentOrgId} ) tinst ON tinst.ITEMINST_ID = ahi_123.ITEMINST_ID
WHERE ahap_123.IS_SERIES_APPROVE = '1'
AND DATE_FORMAT(ahap_123.CREATE_TIME , '%Y-%m-%d')>= #{applyStartTime}
AND DATE_FORMAT(ahap_123.CREATE_TIME , '%Y-%m-%d')<= #{applyEndTime}
AND ahap_123.APPLYINST_SOURCE = #{applySource}
AND ahap_123.IS_SERIES_APPROVE = #{applyType}
AND ahap_123.APPLYINST_STATE = #{applyState}
AND tinst.INST_STATE = #{instState}
) temp_123
AND ( temp_123.projName LIKE CONCAT(CONCAT('%',#{keyword}),'%') OR temp_123.applyinstCode LIKE CONCAT(CONCAT('%',#{keyword}),'%') OR temp_123.localCode LIKE CONCAT(CONCAT('%',#{keyword}),'%') OR temp_123.gcbm LIKE CONCAT(CONCAT('%',#{keyword}),'%') OR temp_123.itemName LIKE CONCAT(CONCAT('%',#{keyword}),'%') OR temp_123.stageName LIKE CONCAT(CONCAT('%',#{keyword}),'%') OR temp_123.linkmanName LIKE CONCAT(CONCAT('%',#{keyword}),'%') )

SELECT * FROM ( SELECT ahi_123.ITEMINST_ID AS iteminstId, ahap_123.APPLYINST_CODE AS applyinstCode, temp1.PROJ_NAME AS projName, temp1.LOCAL_CODE AS localCode, temp1.GCBM AS gcbm, ali_123.LINKMAN_NAME AS linkmanName, '并联' AS applyType, theme.THEME_NAME AS themeName, aps_123.STAGE_NAME AS stageName, ahi_123.ITEMINST_NAME AS itemName, ahap_123.CREATE_TIME AS applyTime, ahi_123.ITEMINST_STATE AS iteminstState, ahap_123.APPLYINST_SOURCE AS applySource, tinst.OVERDUE_TIME AS overdueTime, tinst.REMAINING_TIME AS remainingTime, tinst.INST_STATE AS instState, tinst.TIMERULE_UNIT AS timeruleUnit, ai_123.DUE_NUM AS dueNum FROM AEA_HI_ITEMINST ahi_123 LEFT JOIN AEA_ITEM_BASIC ai_123 ON ahi_123.ITEM_ID = ai_123.ITEM_ID AND ahi_123.ITEM_VER_ID = ai_123.ITEM_VER_ID
INNER JOIN ( SELECT DISTINCT itemhist.ITEMINST_ID FROM aea_log_item_state_hist itemhist WHERE itemhist.OPS_ORG_ID = #{currentOrgId} )hist ON hist.ITEMINST_ID = ahi_123.ITEMINST_ID
