AND theme.THEME_ID = #{theme}
AND aps_123.STAGE_ID = #{approvalStage}
AND DATE_FORMAT(ahap_123.CREATE_TIME , '%Y-%m-%d')>= #{applyStartTime}
AND DATE_FORMAT(ahap_123.CREATE_TIME , '%Y-%m-%d')<= #{applyEndTime}
AND ahap_123.APPLYINST_SOURCE = #{applySource}
AND ahap_123.IS_SERIES_APPROVE = #{applyType}
AND ahi_123.ITEMINST_STATE = #{iteminstState}
AND tinst.INST_STATE = #{instState}
UNION SELECT ahi_123.ITEMINST_ID AS iteminstId, ahap_123.APPLYINST_CODE AS applyinstCode, temp1.PROJ_NAME AS projName, temp1.LOCAL_CODE AS localCode, temp1.GCBM AS gcbm, ali_123.LINKMAN_NAME AS linkmanName, '单项' AS applyType, NULL AS themeName, NULL AS stageName, ahi_123.ITEMINST_NAME AS itemName, ahap_123.CREATE_TIME AS applyTime, ahi_123.ITEMINST_STATE AS iteminstState, ahap_123.APPLYINST_SOURCE AS applySource, tinst.OVERDUE_TIME AS overdueTime, tinst.REMAINING_TIME AS remainingTime, tinst.INST_STATE AS instState, tinst.TIMERULE_UNIT AS timeruleUnit, ai_123.DUE_NUM AS dueNum FROM AEA_HI_ITEMINST ahi_123 LEFT JOIN AEA_ITEM_BASIC ai_123 ON ahi_123.ITEM_ID = ai_123.ITEM_ID AND ahi_123.ITEM_VER_ID = ai_123.ITEM_VER_ID
INNER JOIN ( SELECT DISTINCT itemhist.ITEMINST_ID FROM aea_log_item_state_hist itemhist WHERE itemhist.OPS_ORG_ID = #{currentOrgId} )hist ON hist.ITEMINST_ID = ahi_123.ITEMINST_ID
