LEFT JOIN AEA_LINKMAN_INFO ali_123 ON ali_123.LINKMAN_INFO_ID = ahap_123.LINKMAN_INFO_ID LEFT JOIN ( SELECT asti.INST_STATE, asti.REMAINING_TIME, ahi.ITEMINST_ID, asti.TIMERULE_UNIT, asti.OVERDUE_TIME FROM act_sto_timerule_inst asti, aea_hi_iteminst ahi WHERE asti.PROC_INST_ID = ahi.PROCINST_ID AND asti.ORG_ID = #{currentOrgId} ) tinst ON tinst.ITEMINST_ID = ahi_123.ITEMINST_ID WHERE ahi_123.IS_SERIES_APPROVE = '1'
AND DATE_FORMAT(ahap_123.CREATE_TIME , '%Y-%m-%d')>= #{applyStartTime}
AND DATE_FORMAT(ahap_123.CREATE_TIME , '%Y-%m-%d')<= #{applyEndTime}
