LEFT JOIN AEA_LINKMAN_INFO ali_123 ON ali_123.LINKMAN_INFO_ID = ahap_123.LINKMAN_INFO_ID LEFT JOIN AEA_HI_PAR_STAGEINST ahs_123 ON ahs_123.APPLYINST_ID = ahap_123.APPLYINST_ID LEFT JOIN AEA_PAR_STAGE aps_123 ON aps_123.STAGE_ID = ahs_123.STAGE_ID LEFT JOIN AEA_PAR_THEME_VER ver ON aps_123.THEME_VER_ID = ver.THEME_VER_ID LEFT JOIN aea_par_theme theme ON theme.THEME_ID = ver.THEME_ID LEFT JOIN act_sto_timerule_inst ti ON ti.PROC_INST_ID = AHP_123.PROC_INST_ID_
ahap_123.IS_SERIES_APPROVE = '0'
AND art_123.ASSIGNEE_ = #{loginName} AND ahap_123.APPLYINST_STATE NOT IN ('0', '1', '3', '5', '6')
) UNION ( SELECT art_123.ID_ AS taskId, ahap_123.APPLYINST_ID AS applyinstId, ahap_123.APPLYINST_CODE AS applyinstCode, aps_123.STAGE_NAME AS stageName, ali_123.LINKMAN_NAME AS linkmanName, ahap_123.APPLYINST_SOURCE AS applySource, art_123.CREATE_TIME_ AS arriveTime, ahap_123.ACCEPT_TIME AS acceptTime, ahap_123.CREATE_TIME AS applyTime, art_123.NAME_ AS taskName, ai_123.DUE_NUM AS dueNum, '0' AS isSeriesApprove, hi.ITEMINST_ID AS busRecordId, NULL AS itemProperty, '并联' AS applyType, temp1.PROJ_NAME AS projName, temp1.LOCAL_CODE AS localCode, temp1.GCBM AS gcbm, theme.THEME_NAME AS themeName, hi.ITEMINST_NAME AS itemName, ti.OVERDUE_TIME AS overdueTime, ti.REMAINING_TIME AS remainingTime, ti.INST_STATE AS instState, ti.TIMERULE_UNIT AS timeruleUnit, art_123.SIGN_STATE_ AS signState FROM ACT_RU_TASK art_123 LEFT JOIN ACT_HI_PROCINST ahp_123 ON art_123.PROC_INST_ID_ = AHP_123.PROC_INST_ID_ LEFT JOIN ACT_STO_APPINST_SUBFLOW asas_123 ON asas_123.SUBFLOW_PROCINST_ID = art_123.PROC_INST_ID_ LEFT JOIN ACT_STO_APPINST asa_123 ON asas_123.appinst_id = asa_123.APPINST_ID LEFT JOIN AEA_HI_APPLYINST ahap_123 ON ahap_123.APPLYINST_ID = asa_123.master_record_id
LEFT JOIN AEA_LINKMAN_INFO ali_123 ON ali_123.LINKMAN_INFO_ID = ahap_123.LINKMAN_INFO_ID LEFT JOIN AEA_HI_PAR_STAGEINST ahs_123 ON ahs_123.APPLYINST_ID = ahap_123.APPLYINST_ID LEFT JOIN AEA_PAR_STAGE aps_123 ON aps_123.STAGE_ID = ahs_123.STAGE_ID LEFT JOIN AEA_PAR_THEME_VER ver ON aps_123.THEME_VER_ID = ver.THEME_VER_ID LEFT JOIN aea_par_theme theme ON theme.THEME_ID = ver.THEME_ID LEFT JOIN aea_hi_iteminst hi ON hi.PROCINST_ID = art_123.PROC_INST_ID_ LEFT JOIN AEA_ITEM_BASIC ai_123 ON hi.ITEM_ID = ai_123.ITEM_ID AND hi.ITEM_VER_ID = ai_123.ITEM_VER_ID LEFT JOIN act_sto_timerule_inst ti ON ti.PROC_INST_ID = hi.PROCINST_ID
ahap_123.IS_SERIES_APPROVE = '0' AND hi.ITEMINST_STATE NOT IN ( '1', '2', '4', '5', '6', '14', '15' ) AND ahap_123.APPLYINST_STATE != '0' AND art_123.ASSIGNEE_ = #{loginName}
AND theme.THEME_ID = #{theme}
) UNION ( SELECT art_123.ID_ AS taskId, ahap_123.APPLYINST_ID AS applyinstId, ahap_123.APPLYINST_CODE AS applyinstCode, aps_123.STAGE_NAME AS stageName, ali_123.LINKMAN_NAME AS linkmanName, ahap_123.APPLYINST_SOURCE AS applySource, art_123.CREATE_TIME_ AS arriveTime, ahap_123.ACCEPT_TIME AS acceptTime, ahap_123.CREATE_TIME AS applyTime, art_123.NAME_ AS taskName, aps_123.DUE_NUM AS dueNum, '0' AS isSeriesApprove, NULL AS busRecordId, NULL AS itemProperty, '并联' AS applyType, temp1.PROJ_NAME AS projName, temp1.LOCAL_CODE AS localCode, temp1.GCBM AS gcbm, theme.THEME_NAME AS themeName, NULL AS itemName, ti.OVERDUE_TIME AS overdueTime, ti.REMAINING_TIME AS remainingTime, ti.INST_STATE AS instState, ti.TIMERULE_UNIT AS timeruleUnit, art_123.SIGN_STATE_ AS signState FROM ACT_RU_TASK art_123 LEFT JOIN ACT_HI_PROCINST ahp_123 ON art_123.PROC_INST_ID_ = AHP_123.PROC_INST_ID_ LEFT JOIN ACT_STO_APPINST_SUBFLOW asas_123 ON asas_123.SUBFLOW_PROCINST_ID = art_123.PROC_INST_ID_ LEFT JOIN ACT_STO_APPINST asa_123 ON asas_123.appinst_id = asa_123.APPINST_ID LEFT JOIN AEA_HI_APPLYINST ahap_123 ON ahap_123.APPLYINST_ID = asa_123.master_record_id
