ahap_123.IS_SERIES_APPROVE = '0' AND ahap_123.APPLYINST_STATE IN ('6') AND ahap_123.is_sms_send ='1'
AND ahap_123.IS_SERIES_APPROVE = #{applyType}
UNION
SELECT ahap_123.APPLYINST_CODE applyinstCode, ahap_123.APPLYINST_ID applyinstId, ahap_123.APPLYINST_SOURCE applySource, temp1.PROJ_NAME projName, temp1.LOCAL_CODE AS localCode, temp1.GCBM AS gcbm, ali_123.LINKMAN_NAME linkmanName, '单项' AS applyType, NULL AS themeName, NULL AS themeId, NULL AS stageName, ahi_123.ITEMINST_NAME AS itemName, ahap_123.ACCEPT_TIME acceptTime, ahap_123.MODIFY_TIME concludedTime, ahap_123.IS_SMS_SEND isSmsSend FROM AEA_HI_APPLYINST ahap_123
LEFT JOIN AEA_LINKMAN_INFO ali_123 ON ali_123.LINKMAN_INFO_ID = ahap_123.LINKMAN_INFO_ID LEFT JOIN AEA_HI_SERIESINST ahs_123 ON ahs_123.APPLYINST_ID = ahap_123.APPLYINST_ID LEFT JOIN AEA_HI_ITEMINST ahi_123 ON ahi_123.SERIESINST_ID = ahs_123.SERIESINST_ID
ahap_123.IS_SERIES_APPROVE = '1' AND ahap_123.APPLYINST_STATE IN ('6') AND ahap_123.is_sms_send ='1'
AND ahap_123.IS_SERIES_APPROVE = #{applyType}
) temp_123
AND ( temp_123.projName LIKE CONCAT(CONCAT('%',#{keyword}),'%') OR temp_123.applyinstCode LIKE CONCAT(CONCAT('%',#{keyword}),'%') OR temp_123.localCode LIKE CONCAT(CONCAT('%',#{keyword}),'%') OR temp_123.gcbm LIKE CONCAT(CONCAT('%',#{keyword}),'%') OR temp_123.stageName LIKE CONCAT(CONCAT('%',#{keyword}),'%') OR temp_123.linkmanName LIKE CONCAT(CONCAT('%',#{keyword}),'%') )
AND DATE_FORMAT(temp_123.acceptTime , '%Y-%m-%d')>= #{acceptStartTime}
AND DATE_FORMAT(temp_123.acceptTime , '%Y-%m-%d')<= #{acceptEndTime}
AND DATE_FORMAT(temp_123.concludedTime , '%Y-%m-%d')>= #{concludedStartTime}
AND DATE_FORMAT(temp_123.concludedTime , '%Y-%m-%d')<= #{concludedEndTime}
AND temp_123.applySource = #{applySource}
AND temp_123.themeId = #{theme}

