INNER JOIN ( SELECT DISTINCT alog.APPLYINST_ID FROM aea_log_apply_state_hist alog
AND alog.OPS_WINDOW_ID = #{windowId}
)wi ON wi.APPLYINST_ID = ahap_123.APPLYINST_ID LEFT JOIN AEA_PAR_STAGE aps_123 ON aps_123.STAGE_ID = ahs_123.STAGE_ID LEFT JOIN AEA_PAR_THEME_VER ver ON aps_123.THEME_VER_ID = ver.THEME_VER_ID LEFT JOIN AEA_LINKMAN_INFO ali_123 ON ali_123.LINKMAN_INFO_ID = ahap_123.LINKMAN_INFO_ID LEFT JOIN AEA_PAR_THEME theme ON theme.THEME_ID = ver.THEME_ID INNER JOIN AEA_HI_ITEM_CORRECT ahic_123 ON ahic_123.APPLYINST_ID = ahap_123.APPLYINST_ID AND ahic_123.ITEMINST_ID = ahi_123.ITEMINST_ID AND ahic_123.CORRECT_STATE = '6' LEFT JOIN opu_om_user oou ON ahic_123.CREATER = oou.USER_ID
ahi_123.IS_SERIES_APPROVE = '0' AND ahi_123.ITEMINST_STATE = '6'
UNION SELECT ahic_123.CHARGE_ORG_ID AS orgId, ahic_123.CHARGE_ORG_NAME AS orgName FROM AEA_HI_ITEMINST ahi_123 LEFT JOIN AEA_HI_SERIESINST ahs_123 ON ahi_123.SERIESINST_ID = ahs_123.SERIESINST_ID LEFT JOIN AEA_HI_APPLYINST ahap_123 ON ahs_123.APPLYINST_ID = ahap_123.APPLYINST_ID
INNER JOIN ( SELECT DISTINCT alog.APPLYINST_ID FROM aea_log_apply_state_hist alog
AND alog.OPS_WINDOW_ID = #{windowId}
)wi ON wi.APPLYINST_ID = ahap_123.APPLYINST_ID LEFT JOIN AEA_LINKMAN_INFO ali_123 ON ali_123.LINKMAN_INFO_ID = ahap_123.LINKMAN_INFO_ID INNER JOIN AEA_HI_ITEM_CORRECT ahic_123 ON ahic_123.APPLYINST_ID = ahap_123.APPLYINST_ID AND ahic_123.ITEMINST_ID = ahi_123.ITEMINST_ID AND ahic_123.CORRECT_STATE = '6' LEFT JOIN opu_om_user oou ON ahic_123.CREATER = oou.USER_ID
