SELECT temp_123.* FROM ( SELECT ahi_123.ITEMINST_ID AS iteminstId, ahap_123.APPLYINST_CODE AS applyinstCode, temp1.PROJ_NAME AS projName, temp1.LOCAL_CODE AS localCode, temp1.GCBM AS gcbm, ali_123.LINKMAN_NAME AS linkmanName, case ahap_123.IS_SERIES_APPROVE WHEN '1' THEN '单项' ELSE '并联' end AS applyType, ahap_123.APPLYINST_SOURCE AS applySource, theme.THEME_NAME AS themeName, aps_123.STAGE_NAME AS stageName, ahi_123.ITEMINST_NAME AS itemName, ahap_123.CREATE_TIME AS applyTime, ahic_123.CORRECT_ID AS correctId, ahic_123.CHARGE_ORG_NAME AS organizer, oou.USER_NAME AS assignee, ahic_123.CORRECT_DUE_TIME AS correctDueTime FROM AEA_HI_ITEMINST ahi_123 LEFT JOIN AEA_HI_PAR_STAGEINST ahs_123 ON ahi_123.STAGEINST_ID = ahs_123.STAGEINST_ID LEFT JOIN AEA_HI_APPLYINST ahap_123 ON ahs_123.APPLYINST_ID = ahap_123.APPLYINST_ID
INNER JOIN ( SELECT DISTINCT alog.APPLYINST_ID FROM aea_log_apply_state_hist alog
AND alog.OPS_WINDOW_ID = #{windowId}
)wi ON wi.APPLYINST_ID = ahap_123.APPLYINST_ID
LEFT JOIN AEA_PAR_STAGE aps_123 ON aps_123.STAGE_ID = ahs_123.STAGE_ID LEFT JOIN AEA_PAR_THEME_VER ver ON aps_123.THEME_VER_ID = ver.THEME_VER_ID LEFT JOIN AEA_LINKMAN_INFO ali_123 ON ali_123.LINKMAN_INFO_ID = ahap_123.LINKMAN_INFO_ID LEFT JOIN AEA_PAR_THEME theme ON theme.THEME_ID = ver.THEME_ID INNER JOIN AEA_HI_ITEM_CORRECT ahic_123 ON ahic_123.APPLYINST_ID = ahap_123.APPLYINST_ID AND ahic_123.ITEMINST_ID = ahi_123.ITEMINST_ID AND ahic_123.CORRECT_STATE = '7' LEFT JOIN opu_om_user oou ON ahic_123.CREATER = oou.USER_ID LEFT JOIN ACT_HI_TASKINST aht_123 ON aht_123.PROC_INST_ID_ = ahi_123.PROCINST_ID
ahi_123.IS_SERIES_APPROVE = '0' AND ahi_123.ITEMINST_STATE = '7'
and ahi_123.APPROVE_ORG_ID = #{currentOrgId}
AND theme.THEME_ID = #{theme}
AND DATE_FORMAT(ahic_123.CORRECT_DUE_TIME , '%Y-%m-%d')>= #{correctDueStartTime}
AND DATE_FORMAT(ahic_123.CORRECT_DUE_TIME , '%Y-%m-%d')<= #{correctDueEndTime}
AND ahic_123.CHARGE_ORG_ID = #{organizerId}
UNION SELECT ahi_123.ITEMINST_ID AS iteminstId , ahap_123.APPLYINST_CODE AS applyinstCode, temp1.PROJ_NAME AS projName, temp1.LOCAL_CODE AS localCode, temp1.GCBM AS gcbm, ali_123.LINKMAN_NAME AS linkmanName, case ahap_123.IS_SERIES_APPROVE WHEN '1' THEN '单项' ELSE '并联' end AS applyType, ahap_123.APPLYINST_SOURCE AS applySource, NULL AS themeName, NULL AS stageName, ahi_123.ITEMINST_NAME AS itemName, ahap_123.CREATE_TIME AS applyTime, ahic_123.CORRECT_ID AS correctId, ahic_123.CHARGE_ORG_NAME AS organizer, oou.USER_NAME AS assignee, ahic_123.CORRECT_DUE_TIME AS correctDueTime FROM AEA_HI_ITEMINST ahi_123 LEFT JOIN AEA_HI_SERIESINST ahs_123 ON ahi_123.SERIESINST_ID = ahs_123.SERIESINST_ID LEFT JOIN AEA_HI_APPLYINST ahap_123 ON ahs_123.APPLYINST_ID = ahap_123.APPLYINST_ID
INNER JOIN ( SELECT DISTINCT alog.APPLYINST_ID FROM aea_log_apply_state_hist alog
AND alog.OPS_WINDOW_ID = #{windowId}
)wi ON wi.APPLYINST_ID = ahap_123.APPLYINST_ID
LEFT JOIN AEA_LINKMAN_INFO ali_123 ON ali_123.LINKMAN_INFO_ID = ahap_123.LINKMAN_INFO_ID INNER JOIN AEA_HI_ITEM_CORRECT ahic_123 ON ahic_123.APPLYINST_ID = ahap_123.APPLYINST_ID AND ahic_123.ITEMINST_ID = ahi_123.ITEMINST_ID AND ahic_123.CORRECT_STATE = '7' LEFT JOIN opu_om_user oou ON ahic_123.CREATER = oou.USER_ID LEFT JOIN ACT_HI_TASKINST aht_123 ON aht_123.PROC_INST_ID_ = ahi_123.PROCINST_ID
ahi_123.IS_SERIES_APPROVE = '1' AND ahi_123.ITEMINST_STATE = '7'
and ahi_123.APPROVE_ORG_ID = #{currentOrgId}
AND DATE_FORMAT(ahic_123.CORRECT_DUE_TIME , '%Y-%m-%d')>= #{correctDueStartTime}
AND DATE_FORMAT(ahic_123.CORRECT_DUE_TIME , '%Y-%m-%d')<= #{correctDueEndTime}
AND ahic_123.CHARGE_ORG_ID = #{organizerId}
) temp_123
AND ( temp_123.projName LIKE CONCAT(CONCAT('%',#{keyword}),'%') OR temp_123.applyinstCode LIKE CONCAT(CONCAT('%',#{keyword}),'%') OR temp_123.localCode LIKE CONCAT(CONCAT('%',#{keyword}),'%') OR temp_123.gcbm LIKE CONCAT(CONCAT('%',#{keyword}),'%') OR temp_123.itemName LIKE CONCAT(CONCAT('%',#{keyword}),'%') OR temp_123.stageName LIKE CONCAT(CONCAT('%',#{keyword}),'%') OR temp_123.linkmanName LIKE CONCAT(CONCAT('%',#{keyword}),'%') )

SELECT temp_123.* FROM ( SELECT ahi_123.ITEMINST_ID AS iteminstId, ahap_123.APPLYINST_CODE AS applyinstCode, temp1.PROJ_NAME AS projName, temp1.LOCAL_CODE AS localCode, temp1.GCBM AS gcbm, ali_123.LINKMAN_NAME AS linkmanName, case ahap_123.IS_SERIES_APPROVE WHEN '1' THEN '单项' ELSE '并联' end AS applyType, ahap_123.APPLYINST_SOURCE AS applySource, theme.THEME_NAME AS themeName, aps_123.STAGE_NAME AS stageName, ahi_123.ITEMINST_NAME AS itemName, ahap_123.CREATE_TIME AS applyTime, oou.USER_NAME AS assignee, ahis_123.SPECIAL_ID AS specialId, ahis_123.SPECIAL_TYPE AS specialType, ahis_123.SPECIAL_DUE_WAY AS specialDueWay, ahis_123.CREATE_TIME AS specialCreateTime, ahis_123.SPECIAL_DUE_TIME AS specialDueTime, CONVERT ( MAX( aht_123.ID_ + 0 ), CHAR ) AS taskId, ahi_123.ITEMINST_ID AS busRecordId, aht_123.NAME_ as taskName FROM AEA_HI_ITEMINST ahi_123 LEFT JOIN AEA_HI_PAR_STAGEINST ahs_123 ON ahi_123.STAGEINST_ID = ahs_123.STAGEINST_ID LEFT JOIN AEA_HI_APPLYINST ahap_123 ON ahs_123.APPLYINST_ID = ahap_123.APPLYINST_ID
INNER JOIN ( SELECT DISTINCT alog.APPLYINST_ID FROM aea_log_apply_state_hist alog
AND alog.OPS_WINDOW_ID = #{windowId}
)wi ON wi.APPLYINST_ID = ahap_123.APPLYINST_ID
LEFT JOIN AEA_PAR_STAGE aps_123 ON aps_123.STAGE_ID = ahs_123.STAGE_ID LEFT JOIN AEA_PAR_THEME_VER ver ON aps_123.THEME_VER_ID = ver.THEME_VER_ID LEFT JOIN AEA_LINKMAN_INFO ali_123 ON ali_123.LINKMAN_INFO_ID = ahap_123.LINKMAN_INFO_ID LEFT JOIN AEA_PAR_THEME theme ON theme.THEME_ID = ver.THEME_ID INNER JOIN AEA_HI_ITEM_SPECIAL ahis_123 ON ahis_123.APPLYINST_ID = ahap_123.APPLYINST_ID AND ahis_123.ITEMINST_ID = ahi_123.ITEMINST_ID AND ahis_123.APPROVE_RESULT = '1' and ahis_123.SPECIAL_STATE='9' LEFT JOIN opu_om_user oou ON ahis_123.OPS_USER_ID = oou.USER_ID LEFT JOIN ACT_HI_TASKINST aht_123 ON aht_123.PROC_INST_ID_ = ahi_123.PROCINST_ID
ahi_123.IS_SERIES_APPROVE = '0' AND ahi_123.ITEMINST_STATE = '9'
and ahi_123.APPROVE_ORG_ID = #{currentOrgId}
AND theme.THEME_ID = #{theme}
AND DATE_FORMAT(ahis_123.CREATE_TIME , '%Y-%m-%d')>= #{specialStartTime}
AND DATE_FORMAT(ahis_123.CREATE_TIME , '%Y-%m-%d')<= #{specialEndTime}
AND ahic_123.CHARGE_ORG_ID = #{organizerId}
UNION SELECT ahi_123.ITEMINST_ID AS iteminstId , ahap_123.APPLYINST_CODE AS applyinstCode, temp1.PROJ_NAME AS projName, temp1.LOCAL_CODE AS localCode, temp1.GCBM AS gcbm, ali_123.LINKMAN_NAME AS linkmanName, case ahap_123.IS_SERIES_APPROVE WHEN '1' THEN '单项' ELSE '并联' end AS applyType, ahap_123.APPLYINST_SOURCE AS applySource, NULL AS themeName, NULL AS stageName, ahi_123.ITEMINST_NAME AS itemName, ahap_123.CREATE_TIME AS applyTime, oou.USER_NAME AS assignee, ahis_123.SPECIAL_ID AS specialId, ahis_123.SPECIAL_TYPE AS specialType, ahis_123.SPECIAL_DUE_WAY AS specialDueWay, ahis_123.CREATE_TIME AS specialCreateTime, ahis_123.SPECIAL_DUE_TIME AS specialDueTime, CONVERT ( MAX( aht_123.ID_ + 0 ), CHAR ) AS taskId, ahi_123.ITEMINST_ID AS busRecordId, aht_123.NAME_ as taskName FROM AEA_HI_ITEMINST ahi_123 LEFT JOIN AEA_HI_SERIESINST ahs_123 ON ahi_123.SERIESINST_ID = ahs_123.SERIESINST_ID LEFT JOIN AEA_HI_APPLYINST ahap_123 ON ahs_123.APPLYINST_ID = ahap_123.APPLYINST_ID
INNER JOIN ( SELECT DISTINCT alog.APPLYINST_ID FROM aea_log_apply_state_hist alog
AND alog.OPS_WINDOW_ID = #{windowId}
)wi ON wi.APPLYINST_ID = ahap_123.APPLYINST_ID
LEFT JOIN AEA_LINKMAN_INFO ali_123 ON ali_123.LINKMAN_INFO_ID = ahap_123.LINKMAN_INFO_ID INNER JOIN AEA_HI_ITEM_SPECIAL ahis_123 ON ahis_123.APPLYINST_ID = ahap_123.APPLYINST_ID AND ahis_123.ITEMINST_ID = ahi_123.ITEMINST_ID AND ahis_123.APPROVE_RESULT = '1' and ahis_123.SPECIAL_STATE='9' LEFT JOIN opu_om_user oou ON ahis_123.OPS_USER_ID = oou.USER_ID LEFT JOIN ACT_HI_TASKINST aht_123 ON aht_123.PROC_INST_ID_ = ahi_123.PROCINST_ID
ahi_123.IS_SERIES_APPROVE = '1' AND ahi_123.ITEMINST_STATE = '9'
and ahi_123.APPROVE_ORG_ID = #{currentOrgId}
AND DATE_FORMAT(ahis_123.CREATE_TIME , '%Y-%m-%d')>= #{specialStartTime}
AND DATE_FORMAT(ahis_123.CREATE_TIME , '%Y-%m-%d')<= #{specialEndTime}
AND ahic_123.CHARGE_ORG_ID = #{organizerId}
) temp_123
AND ( temp_123.projName LIKE CONCAT(CONCAT('%',#{keyword}),'%') OR temp_123.applyinstCode LIKE CONCAT(CONCAT('%',#{keyword}),'%') OR temp_123.localCode LIKE CONCAT(CONCAT('%',#{keyword}),'%') OR temp_123.gcbm LIKE CONCAT(CONCAT('%',#{keyword}),'%') OR temp_123.itemName LIKE CONCAT(CONCAT('%',#{keyword}),'%') OR temp_123.stageName LIKE CONCAT(CONCAT('%',#{keyword}),'%') OR temp_123.linkmanName LIKE CONCAT(CONCAT('%',#{keyword}),'%') )
