select asw.WINDOW_NAME windowName ,aha.applyinst_id applyinstId ,aha.APPLYINST_STATE applyinstState ,aha.IS_SERIES_APPROVE isApprove ,aha.APPLYINST_SOURCE applyinstSource from aea_hi_applyinst aha LEFT JOIN opu_om_user oou on oou.login_name = aha.creater LEFT JOIN aea_service_window_user aswu on aswu.user_id = oou.user_id and aswu.ROOT_ORG_ID = #{currentOrgId} LEFT JOIN aea_service_window asw on asw.window_id = aswu.window_id and asw.ROOT_ORG_ID = #{currentOrgId} where aha.CREATE_TIME >= STR_TO_DATE(concat(#{startTime},' 00:00:01'), '%Y-%m-%d %H:%i:%s') and aha.CREATE_TIME <= STR_TO_DATE(concat(#{endTime},' 23:59:59'), '%Y-%m-%d %H:%i:%s') and aha.IS_DELETED='0' and aha.APPLYINST_SOURCE = 'win' and asw.window_name is not null and aha.root_org_id = #{currentOrgId}
UNION select IFNULL(asw.WINDOW_NAME,'未知窗口2')windowName ,aha.applyinst_id applyinstId ,aha.APPLYINST_STATE applyinstState ,aha.IS_SERIES_APPROVE isApprove ,aha.APPLYINST_SOURCE applyinstSource from aea_hi_applyinst aha LEFT JOIN aea_applyinst_proj aap on aap.applyinst_id = aha.applyinst_id LEFT JOIN aea_proj_info api on api.proj_info_id = aap.proj_info_id and api.ROOT_ORG_ID = #{currentOrgId}
left join aea_service_window asw on asw.REGION_ID = api.REGIONALISM and asw.ROOT_ORG_ID = #{currentOrgId} where aha.CREATE_TIME >= STR_TO_DATE(concat(#{startTime},' 00:00:01'), '%Y-%m-%d %H:%i:%s') and aha.CREATE_TIME <= STR_TO_DATE(concat(#{endTime},' 23:59:59'), '%Y-%m-%d %H:%i:%s') and aha.APPLYINST_SOURCE = 'net' and aha.IS_DELETED='0' and asw.IS_ACTIVE='1' and api.REGIONALISM is not null and aha.root_org_id = #{currentOrgId}

select aha.applyinst_id applyinstId ,aha.IS_SERIES_APPROVE isApprove,aap.proj_info_id projInfoId from aea_hi_applyinst aha
LEFT JOIN aea_applyinst_proj aap on aap.applyinst_id = aha.applyinst_id
   where aha.is_deleted = '0' and aha.root_org_id = #{rootOrgId} and aap.proj_info_id is not null

       and aha.applyinst_state in


