               #{item}

select ahi.APPROVE_ORG_ID orgId ,ooo.org_name orgName ,ahi.ITEMINST_ID iteminstId ,ahi.iteminst_state iteminstState ,bdr.REGION_NAME regionName ,bdr.REGION_ID regionId from aea_hi_iteminst ahi LEFT JOIN opu_om_org ooo on ooo.ORG_ID=ahi.APPROVE_ORG_ID LEFT JOIN bsc_dic_region bdr on bdr.region_id = ooo.region_id where 1=1
and ahi.CREATE_TIME >= STR_TO_DATE(concat(#{startTime},' 00:00:01'), '%Y-%m-%d %H:%i:%s')
and ahi.CREATE_TIME <= STR_TO_DATE(concat(#{endTime},' 23:59:59'), '%Y-%m-%d %H:%i:%s')
and ahi.root_org_id = #{rootOrgId}
and ahi.ITEMINST_STATE in
#{item}
#{item}

select gg.*
,ooo.org_name orgName
,bdr.REGION_NAME regionName
,bdr.REGION_ID regionId
from(
select
alish.ITEMINST_ID iteminstId
,alish.NEW_STATE iteminstState
,ahi.APPROVE_ORG_ID orgId
from  aea_hi_iteminst ahi ,aea_log_item_state_hist alish
where alish.ITEMINST_ID = ahi.ITEMINST_ID
and ahi.ROOT_ORG_ID= #{rootOrgId}
and alish.ROOT_ORG_ID = #{rootOrgId}
and alish.TRIGGER_TIME  >=  #{startTime}
and alish.TRIGGER_TIME  <=  #{endTime}

    and alish.NEW_STATE in


            #{item}




            #{item}




)gg
LEFT JOIN opu_om_org ooo on ooo.ORG_ID=gg.orgId
LEFT JOIN bsc_dic_region bdr on bdr.region_id = ooo.region_id
