and ahi.CREATE_TIME <= STR_TO_DATE(concat(#{endTime},' 23:59:59'), '%Y-%m-%d %H:%i:%s')
and ahi.root_org_id = #{rootOrgId}
and ahi.ITEMINST_STATE in
#{item}
#{item}

