and ahi.ROOT_ORG_ID= #{rootOrgId}
and alish.ROOT_ORG_ID = #{rootOrgId}
and alish.TRIGGER_TIME  >=  #{startTime}
and alish.TRIGGER_TIME  <=  #{endTime}

    and alish.NEW_STATE in


            #{item}




            #{item}




)gg
