LEFT JOIN bsc_dic_region bdr on bdr.region_id = ooo.region_id

SELECT count(1)count ,gg.INST_STATE instState from (
select
alash.APPLYINST_ID applyinstId,
aha.IS_SERIES_APPROVE isApprove,
asti.INST_STATE
from
aea_log_apply_state_hist alash
LEFT JOIN aea_hi_applyinst aha on aha.APPLYINST_ID= alash.APPLYINST_ID
LEFT JOIN aea_hi_par_stageinst ahps on ahps.APPLYINST_ID=aha.APPLYINST_ID
LEFT JOIN act_sto_appinst asp on asp.APPINST_ID = ahps.APPINST_ID
LEFT JOIN act_sto_timerule_inst asti on asti.APP_FLOWDEF_ID = asp.APP_FLOWDEF_ID AND asti.PROC_INST_ID = asp.PROCINST_ID
where alash.NEW_STATE='6'
and aha.IS_SERIES_APPROVE='0'
and alash.TRIGGER_TIME  >=  #{startTime}
and alash.TRIGGER_TIME  <=  #{endTime}

    and alash.OPS_WINDOW_ID = #{windowId}

and alash.ROOT_ORG_ID= #{rootOrgId}
and ahps.ROOT_ORG_ID=#{rootOrgId}
and aha.ROOT_ORG_ID = #{rootOrgId}
and asp.ROOT_ORG_ID= #{rootOrgId}
and asti.ORG_ID = #{rootOrgId}
UNION
select
alash.APPLYINST_ID applyinstId,
aha.IS_SERIES_APPROVE isApprove,
asti.INST_STATE
from
aea_log_apply_state_hist alash
LEFT JOIN aea_hi_applyinst aha on aha.APPLYINST_ID= alash.APPLYINST_ID
LEFT JOIN aea_hi_seriesinst ahs on ahs.APPLYINST_ID=aha.APPLYINST_ID
LEFT JOIN aea_hi_iteminst ahi on ahi.SERIESINST_ID = ahs.SERIESINST_ID
LEFT JOIN act_sto_appinst asp on asp.APPINST_ID = ahs.APPINST_ID
LEFT JOIN  act_sto_timerule_inst asti on  asti.PROC_INST_ID = ahi.PROCINST_ID
where alash.NEW_STATE='6'
and aha.IS_SERIES_APPROVE='1'
and ahs.STAGE_ID is not null
and alash.TRIGGER_TIME  >=  #{startTime}
and alash.TRIGGER_TIME  <=  #{endTime}

    and alash.OPS_WINDOW_ID = #{windowId}

and alash.ROOT_ORG_ID= #{rootOrgId}
and ahs.ROOT_ORG_ID =#{rootOrgId}
and ahi.ROOT_ORG_ID =#{rootOrgId}
and asp.ROOT_ORG_ID =#{rootOrgId}
and asti.ORG_ID = #{rootOrgId}
) gg GROUP by gg.INST_STATE

select
alash.APPLYINST_ID applyinstId,
aha.IS_SERIES_APPROVE isApprove,
alash.OPS_WINDOW_ID windowId,
asw.WINDOW_NAME windowName,
asw.SORT_NO sortNO,
aps.DYBZSPJDXH dybzspjdxh,
asti.USE_LIMIT_TIME useLimitTime,
"0"  isParallel
from aea_log_apply_state_hist alash
LEFT JOIN aea_hi_applyinst aha on aha.APPLYINST_ID= alash.APPLYINST_ID
LEFT JOIN aea_service_window asw on alash.OPS_WINDOW_ID=asw.WINDOW_ID
LEFT JOIN aea_hi_par_stageinst ahps on ahps.APPLYINST_ID=aha.APPLYINST_ID
LEFT JOIN aea_par_stage aps on aps.STAGE_ID = ahps.STAGE_ID
LEFT JOIN act_sto_appinst asp on asp.APPINST_ID = ahps.APPINST_ID
LEFT JOIN act_sto_timerule_inst asti on asti.APP_FLOWDEF_ID = asp.APP_FLOWDEF_ID AND asti.PROC_INST_ID = asp.PROCINST_ID
where alash.NEW_STATE='6'
and aha.IS_SERIES_APPROVE='0'
and alash.TRIGGER_TIME  >=  #{startTime}
and alash.TRIGGER_TIME <=  #{endTime}

    and alash.OPS_WINDOW_ID = #{windowId}

and alash.ROOT_ORG_ID = #{rootOrgId}
and aha.ROOT_ORG_ID=#{rootOrgId}
and asw.ROOT_ORG_ID= #{rootOrgId}
and ahps.ROOT_ORG_ID= #{rootOrgId}
and aps.ROOT_ORG_ID= #{rootOrgId}
and asp.ROOT_ORG_ID = #{rootOrgId}
and asti.ORG_ID = #{rootOrgId}
UNION
select
alash.APPLYINST_ID applyinstId,
aha.IS_SERIES_APPROVE isApprove,
alash.OPS_WINDOW_ID windowId,
asw.WINDOW_NAME windowName,
asw.SORT_NO sortNO,
aps.DYBZSPJDXH dybzspjdxh,
asti.USE_LIMIT_TIME useLimitTime,
ahs.IS_PARALLEL isParallel
from aea_log_apply_state_hist alash
LEFT JOIN aea_hi_applyinst aha on aha.APPLYINST_ID= alash.APPLYINST_ID
LEFT JOIN aea_service_window asw on alash.OPS_WINDOW_ID=asw.WINDOW_ID
LEFT JOIN aea_hi_seriesinst ahs on ahs.APPLYINST_ID=aha.APPLYINST_ID
LEFT JOIN aea_hi_iteminst ahi on ahi.SERIESINST_ID = ahs.SERIESINST_ID
LEFT JOIN aea_par_stage aps on aps.STAGE_ID = ahs.STAGE_ID
LEFT JOIN act_sto_appinst asp on asp.APPINST_ID = ahs.APPINST_ID
LEFT JOIN  act_sto_timerule_inst asti on  asti.PROC_INST_ID = ahi.PROCINST_ID
where alash.NEW_STATE='6'
and aha.IS_SERIES_APPROVE='1'
and ahs.STAGE_ID is not null
and alash.TRIGGER_TIME  >=  #{startTime}
and alash.TRIGGER_TIME <=  #{endTime}

    and alash.OPS_WINDOW_ID = #{windowId}

and alash.ROOT_ORG_ID = #{rootOrgId}
and aha.ROOT_ORG_ID= #{rootOrgId}
and asw.ROOT_ORG_ID= #{rootOrgId}
and ahs.ROOT_ORG_ID= #{rootOrgId}
and aps.ROOT_ORG_ID= #{rootOrgId}
and asp.ROOT_ORG_ID = #{rootOrgId}
and asti.ORG_ID = #{rootOrgId}

select THEME_ID as themeId, THEME_NAME as themeName, max(USE_LIMIT_TIME) as maxUseTime, min(USE_LIMIT_TIME) as minUseTime, round(avg(USE_LIMIT_TIME), 2) as avgUseTime from ( select apt.THEME_ID, apt.THEME_NAME, asti.USE_LIMIT_TIME from ACT_STO_TIMERULE_INST asti, ACT_STO_APPINST asp, AEA_HI_PAR_STAGEINST ahps, AEA_PAR_STAGE aps, AEA_PAR_THEME_VER aptv, AEA_PAR_THEME apt, AEA_HI_APPLYINST aha, AEA_LOG_APPLY_STATE_HIST alash where asti.APP_FLOWDEF_ID = asp.APP_FLOWDEF_ID and asti.PROC_INST_ID = asp.PROCINST_ID and asp.APPINST_ID = ahps.APPINST_ID and ahps.STAGE_ID = aps.STAGE_ID and aps.THEME_VER_ID = aptv.THEME_VER_ID and aptv.THEME_ID = apt.THEME_ID and ahps.APPLYINST_ID = aha.APPLYINST_ID and aha.APPLYINST_ID = alash.APPLYINST_ID and asti.ORG_ID = #{rootOrgId} and asp.ROOT_ORG_ID = #{rootOrgId} and ahps.ROOT_ORG_ID = #{rootOrgId} and aha.ROOT_ORG_ID = #{rootOrgId}
and to_char(alash.TRIGGER_TIME, 'yyyy-mm-dd HH24:mi:ss') >= #{startTime}
and to_char(alash.TRIGGER_TIME, 'yyyy-mm-dd HH24:mi:ss') <= #{endTime}
and alash.TRIGGER_TIME >= #{startTime}
and alash.TRIGGER_TIME <= #{endTime}
and alash.NEW_STATE = '6' and aha.APPLYINST_STATE = '6' and aha.IS_DELETED = '0' and aha.IS_SERIES_APPROVE= '0' union all select apt.THEME_ID, apt.THEME_NAME, asti.USE_LIMIT_TIME from ACT_STO_TIMERULE_INST asti, AEA_HI_ITEMINST ahi, AEA_HI_SERIESINST ahs, AEA_PAR_STAGE aps, AEA_PAR_THEME_VER aptv, AEA_PAR_THEME apt, AEA_HI_APPLYINST aha, AEA_LOG_APPLY_STATE_HIST alash where ahi.SERIESINST_ID = ahs.SERIESINST_ID and asti.PROC_INST_ID = ahi.PROCINST_ID and ahs.STAGE_ID = aps.STAGE_ID and aps.THEME_VER_ID = aptv.THEME_VER_ID and aptv.THEME_ID = apt.THEME_ID and ahs.APPLYINST_ID = aha.APPLYINST_ID and aha.APPLYINST_ID = alash.APPLYINST_ID and asti.ORG_ID = #{rootOrgId} and ahi.ROOT_ORG_ID = #{rootOrgId} and ahs.ROOT_ORG_ID = #{rootOrgId} and aha.ROOT_ORG_ID = #{rootOrgId}
and to_char(alash.TRIGGER_TIME, 'yyyy-mm-dd HH24:mi:ss') >= #{startTime}
and to_char(alash.TRIGGER_TIME, 'yyyy-mm-dd HH24:mi:ss') <= #{endTime}
and alash.TRIGGER_TIME >= #{startTime}
and alash.TRIGGER_TIME <= #{endTime}
and alash.NEW_STATE = '6' and aha.APPLYINST_STATE = '6' and aha.IS_DELETED = '0' ) a group by THEME_ID, THEME_NAME

select OPS_WINDOW_ID as windowId, WINDOW_NAME as windowName, max(USE_LIMIT_TIME) as maxUseTime, min(USE_LIMIT_TIME) as minUseTime, round(avg(USE_LIMIT_TIME), 2) as avgUseTime from ( select alash.OPS_WINDOW_ID, win.WINDOW_NAME, asti.USE_LIMIT_TIME from ACT_STO_TIMERULE_INST asti, ACT_STO_APPINST asp, AEA_HI_PAR_STAGEINST ahps, AEA_PAR_STAGE aps, AEA_PAR_THEME_VER aptv, AEA_PAR_THEME apt, AEA_HI_APPLYINST aha, AEA_LOG_APPLY_STATE_HIST alash, AEA_SERVICE_WINDOW win where asti.APP_FLOWDEF_ID = asp.APP_FLOWDEF_ID and asti.PROC_INST_ID = asp.PROCINST_ID and asp.APPINST_ID = ahps.APPINST_ID and ahps.STAGE_ID = aps.STAGE_ID and aps.THEME_VER_ID = aptv.THEME_VER_ID and aptv.THEME_ID = apt.THEME_ID and ahps.APPLYINST_ID = aha.APPLYINST_ID and aha.APPLYINST_ID = alash.APPLYINST_ID and alash.OPS_WINDOW_ID = win.WINDOW_ID and asti.ORG_ID = #{rootOrgId} and asp.ROOT_ORG_ID = #{rootOrgId} and ahps.ROOT_ORG_ID = #{rootOrgId} and aha.ROOT_ORG_ID = #{rootOrgId} and apt.THEME_ID = #{themeId}
and to_char(alash.TRIGGER_TIME, 'yyyy-mm-dd HH24:mi:ss') >= #{startTime}
and to_char(alash.TRIGGER_TIME, 'yyyy-mm-dd HH24:mi:ss') <= #{endTime}
and alash.TRIGGER_TIME >= #{startTime}
and alash.TRIGGER_TIME <= #{endTime}
and alash.NEW_STATE = '6' and aha.APPLYINST_STATE = '6' and aha.IS_DELETED = '0' and aha.IS_SERIES_APPROVE= '0' union all select alash.OPS_WINDOW_ID, win.WINDOW_NAME, asti.USE_LIMIT_TIME from ACT_STO_TIMERULE_INST asti, AEA_HI_ITEMINST ahi, AEA_HI_SERIESINST ahs, AEA_PAR_STAGE aps, AEA_PAR_THEME_VER aptv, AEA_PAR_THEME apt, AEA_HI_APPLYINST aha, AEA_LOG_APPLY_STATE_HIST alash, AEA_SERVICE_WINDOW win where ahi.SERIESINST_ID = ahs.SERIESINST_ID and asti.PROC_INST_ID = ahi.PROCINST_ID and ahs.STAGE_ID = aps.STAGE_ID and aps.THEME_VER_ID = aptv.THEME_VER_ID and aptv.THEME_ID = apt.THEME_ID and ahs.APPLYINST_ID = aha.APPLYINST_ID and aha.APPLYINST_ID = alash.APPLYINST_ID and alash.OPS_WINDOW_ID = win.WINDOW_ID and asti.ORG_ID = #{rootOrgId} and ahi.ROOT_ORG_ID = #{rootOrgId} and ahs.ROOT_ORG_ID = #{rootOrgId} and aha.ROOT_ORG_ID = #{rootOrgId} and apt.THEME_ID = #{themeId}
and to_char(alash.TRIGGER_TIME, 'yyyy-mm-dd HH24:mi:ss') >= #{startTime}
and to_char(alash.TRIGGER_TIME, 'yyyy-mm-dd HH24:mi:ss') <= #{endTime}
and alash.TRIGGER_TIME >= #{startTime}
and alash.TRIGGER_TIME <= #{endTime}
and alash.NEW_STATE = '6' and aha.APPLYINST_STATE = '6' and aha.IS_DELETED = '0' ) a group by OPS_WINDOW_ID, WINDOW_NAME

select from bsc_dic_region bdr where bdr.REGION_SEQ like concat(#{regionSeq},'%' )
and bdr.REGIONAL_LEVEL = #{level}
ORDER BY bdr.REGIONAL_LEVEL

select

from OPU_OM_ORG org


        AND org.ORG_SEQ like CONCAT (CONCAT('%.',#{orgId}), '.%')


        AND ORG_CODE like CONCAT (CONCAT('%',#{orgCode}), '%')


        AND  ORG_NAME like CONCAT (CONCAT('%',#{orgName}), '%')


        AND ORG_PROPERTY = #{orgProperty}


        AND ORG_RANK = #{orgRank}


        AND IS_ACTIVE = #{isActive}


        AND IS_TOP_ORG = #{isTopOrg}


        AND PARENT_ORG_ID = #{parentOrgId}



        AND region_id = #{regionId}


        AND ORG_LEVEL = #{orgLevel}


        AND IS_PUBLIC = #{isPublic}


        AND (
        ORG_NAME LIKE CONCAT (CONCAT('%',#{keyword}), '%') OR
        ORG_CODE LIKE CONCAT (CONCAT('%',#{keyword}), '%')
        )

    AND  ORG_DELETED='0'
    AND  ORG_ID != '-1'

ORDER BY CREATE_TIME DESC ,MODIFY_TIME DESC
