mapper.select:
select



FROM AEA_ITEM i
WHERE i.PARENT_ITEM_ID = #{itemId}

SELECT apt_123.theme_code AS splcbm, aptv_123.ver_num AS splcbbh , CASE WHEN AEA_PAR_STAGE_ITEM.IS_OPTION_ITEM = '1' THEN 5 WHEN stage.IS_NODE IN ('2', '3') THEN 5 ELSE stage.SORT_NO END AS SPJDXH FROM AEA_PAR_STAGE_ITEM INNER JOIN AEA_PAR_STAGE stage ON stage.STAGE_ID = AEA_PAR_STAGE_ITEM.STAGE_ID INNER JOIN AEA_PAR_THEME_VER aptv_123 ON aptv_123.THEME_VER_ID = stage.THEME_VER_ID INNER JOIN AEA_PAR_THEME apt_123 ON apt_123.THEME_ID = aptv_123.THEME_ID INNER JOIN AEA_ITEM_VER aiv_123 ON aiv_123.ITEM_VER_ID = AEA_PAR_STAGE_ITEM.ITEM_VER_ID INNER JOIN AEA_ITEM_BASIC aib_123 ON aib_123.ITEM_VER_ID = AEA_PAR_STAGE_ITEM.ITEM_VER_ID WHERE aib_123.ITEM_CODE = #{itemCode}
AND apt_123.THEME_CODE = #{themeCode}
ORDER BY AEA_PAR_STAGE_ITEM.CREATE_TIME DESC LIMIT 1
