/**************修改****************/
update act_sto_widget
set WIDGET_NAME='单行文本框'
where WIDGET_ID='20001';
update act_sto_widget_key
set VALUE_TIPS='单行文本框'
,VALUE_DEFAULT='单行文本框'
where KEY_ID='2000101';
update act_sto_widget
set WIDGET_NAME='多行文本框'
where WIDGET_ID='20003';
update act_sto_widget_key
set VALUE_TIPS='多行文本框'
,VALUE_DEFAULT='多行文本框'
where KEY_ID='2000301';

select * from act_sto_widget
where WIDGET_ID='20001'
or  WIDGET_ID='20003';
select * from act_sto_widget_key
where KEY_ID='2000101'
or KEY_ID='2000301';


/*数据字典*/
SELECT	*FROM bsc_dic_code_type
WHERE TYPE_CODE IN('COMMON_POS','COMMON_SEX');
SELECT 	*FROM 	bsc_dic_code_item
WHERE TYPE_ID IN (
		SELECT type_id FROM bsc_dic_code_type
