INSERT INTO `bsc_rule_code` (`CODE_ID`, `CODE_IC`, `CODE_NAME`, `CODE_VALUE_NEXT`, `CODE_VALUE_INCREASE`, `CODE_VALUE_CHAR_COUNT`, `CODE_TEMPLATE`, `CODE_MEMO`, `ORG_ID`) select LOWER(UUID()), iteminst_code, '办件编号', count(1) + 1, 1, 4 , concat(replace(substr(create_time, 1, 10), '-', ''), '{value}'), replace(substr(create_time, 1, 10), '-', ''), root_org_id from aea_hi_iteminst t group by t.iteminst_code, t.root_org_id, substr(create_time, 1, 10) ;
INSERT INTO `bsc_rule_code` (`CODE_ID`, `CODE_IC`, `CODE_NAME`, `CODE_VALUE_NEXT`, `CODE_VALUE_INCREASE`, `CODE_VALUE_CHAR_COUNT`, `CODE_TEMPLATE`, `CODE_MEMO`, `ORG_ID`) select LOWER(UUID()), local_code, '工程编码', count(1) + 1, 1, 4, '-{value}', '工程编码', root_org_id from aea_proj_info t where proj_flag='c' and local_code is not null group by local_code, root_org_id ;
update aea_hi_iteminst t0 join ( select @x:=@x+1 varnum, if (@xcode=iteminst_code and @xtime=substr(t.create_time, 1, 10), @x, @x:=1) cnt, @xcode:=iteminst_code, @xtime:=substr(t.create_time, 1, 10) daystr, t.iteminst_code, t.create_time, t.root_org_id from (select iteminst_code, create_time, root_org_id from aea_hi_iteminst order by iteminst_code asc, create_time asc, root_org_id asc ) t, (select @x:=0, @xcode:=null, @xtime:=null) t2 ) t1 on t0.iteminst_code=t1.iteminst_code and t0.root_org_id=t1.root_org_id and t0.create_time=t1.create_time set t0.iteminst_code=CONCAT(t0.iteminst_code, replace(substr(t1.daystr, 1, 10), '-', ''), lpad(t1.cnt, 4, '0')) ;

