alter table aea_unit_info add indu_comm_reg_num varchar(64) default null comment '工商登记号';
alter table aea_unit_info add organizational_code varchar(64) default null comment '组织机构代码';
alter table aea_unit_info add unified_social_credit_code varchar(64) default null comment '统一社会信用代码';

update aea_unit_info set indu_comm_reg_num=idcard where idtype='1';
update aea_unit_info set organizational_code=idcard where idtype in('3', '4');
update aea_unit_info set unified_social_credit_code=idcard where idtype='2';

