OREIGN KEY (`STAGE_ID`) REFERENCES `aea_par_stage` (`STAGE_ID`), CONSTRAINT `guidance_details_theme_id` FOREIGN KEY (`THEME_ID`) REFERENCES `aea_par_theme` (`THEME_ID`), CONSTRAINT `guidance_details_theme_ver_id` FOREIGN KEY (`THEME_VER_ID`) REFERENCES `aea_par_theme_ver` (`THEME_VER_ID`), CONSTRAINT `guidance_details_user_id` FOREIGN KEY (`GUIDE_USER_ID`) REFERENCES `opu_om_user` (`USER_ID`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8;

ALTER TABLE `aea_proj_apply_agent`
MODIFY COLUMN `AGENT_APPLY_STATE` char(1) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL
COMMENT '代办申请状态 1待签订 2窗口签订中 3 待申请人签章 4已签订（代办中） 5代办终止 6不予受理 7代办结束' AFTER `AGENT_STAGE_STATE`;

ALTER TABLE `aea_proj_apply_agent`
ADD COLUMN `AGENT_STOP_AGREEMENT_FILE_ID` varchar(50) NULL COMMENT '代办终止协议文件ID' AFTER `AGENT_USER_MOBILE`;

ALTER TABLE `aea_proj_apply_agent`
ADD COLUMN `AGENT_END_AGREEMENT_FILE_ID` varchar(11) NULL COMMENT '代办结束办结单文件ID' AFTER `AGENT_STOP_AGREEMENT_FILE_ID`;

ALTER TABLE `aea_hi_sms_send_item`
MODIFY COLUMN `INOUTINST_ID` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT
'输出材料实例id，可能多个' AFTER `ITEMINST_ID`;

ALTER TABLE `aea_proj_apply_agent`
MODIFY COLUMN `AGENT_APPLY_STATE` char(1) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL
COMMENT '代办申请状态 1待签订 2窗口签订中 3 待申请人签章 4已签订（代办中） 5不予受理 6窗口终止 7代办终止 8窗口办结 9代办结束' AFTER `AGENT_STAGE_STATE`;

ALTER TABLE `aea_proj_apply_agent`
